import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Post from '../components/Post'; // นำ component Post มาใช้เพื่อแสดงโพสต์ในหน้าโปรไฟล์

const Profile = () => {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');
  
    if (!token) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }
  
    // ดึงข้อมูลผู้ใช้
    axios.get('http://localhost:5000/pawpaw_test/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      setUser(response.data);  // ตั้งค่าผู้ใช้
    })
    .catch(error => {
      console.error('Error fetching user data:', error);
      alert('Failed to load user profile. Please try again.');
      navigate('/login');
    });
  
    // ดึงโพสต์ของผู้ใช้
    axios.get(`http://localhost:5000/pawpaw_test/posts/user/${userId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      setPosts(response.data.posts);  // ตั้งค่าโพสต์
    })
    .catch(error => {
      console.error('Error fetching user posts:', error);
    });
  
  }, [navigate]);  // ตรวจสอบให้แน่ใจว่า `user` และ `posts` ได้รับการโหลดก่อนใช้งาน
  

  if (!user) {
    return null; // ไม่แสดงอะไรถ้ายังไม่มีข้อมูลผู้ใช้หรือยังไม่ได้ล็อกอิน
  }

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        <div style={styles.profileHeader}>
        <img
          src={user.profileImage || '/default-profile.png'}  // ใช้ default ถ้าไม่มีรูป
          alt="Profile"
          style={styles.profileImage}
        />
        <h2>{user.username || 'Unknown User'}</h2>  // ใช้ 'Unknown User' ถ้าไม่มี username

          <h2>{user.username}</h2>
          <p>{user.profession || 'No profession provided'}</p>
        </div>
        <div style={styles.aboutSection}>
          <h3>About</h3>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone || 'No phone number provided'}</p>
          <p><strong>Location:</strong> {user.location || 'No location provided'}</p>
        </div>
      </div>

      <div style={styles.posts}>
        <h3>Posts</h3>
        {posts && posts.length > 0 ? (
          posts.map(post => (
            <Post key={post._id} post={post} /> // ใช้ Component Post ในการแสดงโพสต์
          ))
        ) : (
          <p>No posts available. Try posting something about your pet!</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileHeader: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  profileImage: {
    borderRadius: '50%',
    width: '120px',
    height: '120px',
    objectFit: 'cover',
  },
  aboutSection: {
    marginTop: '20px',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
};

export default Profile;
