import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, Typography, Box, Divider } from '@mui/material';

const Comment = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  // Fetch comments when component mounts
  useEffect(() => {
    fetchComments();
  }, [postId]);

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleAddComment = async () => {
    if (newComment.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
        { content: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setComments([response.data.comment, ...comments]);
      setNewComment('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  return (
    <Box sx={{ padding: '16px' }}>
      {/* Input for adding a new comment */}
      <TextField
        label="Write a comment..."
        variant="outlined"
        multiline
        rows={4}
        fullWidth
        value={newComment}
        onChange={(e) => setNewComment(e.target.value)}
        sx={{
          marginBottom: '16px',
          '& .MuiOutlinedInput-root': {
            borderRadius: '8px',
          },
        }}
      />
      <Button
        onClick={handleAddComment}
        variant="contained"
        color="primary"
        sx={{
          display: 'block',
          width: '100%',
          borderRadius: '8px',
          padding: '12px 0',
        }}
      >
        Post Comment
      </Button>

      <Divider sx={{ margin: '16px 0' }} />

      {/* Display the list of comments */}
      {comments.map((comment) => (
        <Box key={comment._id} sx={{ marginBottom: '12px' }}>
          <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
            {comment.userId.username}
          </Typography>
          <Typography variant="body2" sx={{ marginTop: '4px' }}>
            {comment.content}
          </Typography>
        </Box>
      ))}
    </Box>
  );
};

export default Comment;
