import React, { useState, useEffect } from 'react';
import { Card, Box, Typography, TextField, Button, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';
import Comment from './Comment'; // ใช้ Comment.js สำหรับการแสดงคอมเมนต์
import DeleteButton from './DeleteButton';
import { shareContent } from './shareUtil';

const Post = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [currentUserId, setCurrentUserId] = useState(null);
  const [comment, setComment] = useState('');
  const [postState, setPostState] = useState({
    ...post,
    likes: Array.isArray(post.likes) ? post.likes : [],
  });

  const [showComments, setShowComments] = useState(false);

  // Fetch user ID and comments when the component mounts
  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      setCurrentUserId(userId);
    } else {
      console.error('User ID is not set in localStorage');
    }

    const fetchComments = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/pawpaw_test/posts/${postState._id}/comments`
        );
        setComments(response.data.comments); // Set comments from backend response
      } catch (error) {
        console.error('Failed to fetch comments:', error);
      }
    };

    fetchComments();
  }, [postState._id]);

  const handleAddComment = async () => {
    if (comment.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postState._id}/comment`,
        { content: comment },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // รีเฟรชคอมเมนต์ใหม่จาก backend
      fetchComments();
      setComment('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  // Toggle comments visibility
  const toggleComments = () => {
    setShowComments((prevShowComments) => !prevShowComments);
  };

  const handleLikePost = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to like the post.');
        return false;
      }

      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postState._id}/like`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.data.success) {
        setPostState((prevState) => ({
          ...prevState,
          likes: response.data.likes, // อัปเดตจำนวนไลค์ใน state
        }));
        return true;
      }
    } catch (error) {
      console.error('Failed to like/unlike the post', error);
    }
    return false;
  };

  const handleSharePost = () => {
    shareContent({
      title: postState.content || 'Check out this post!',
      text: postState.content,
      url: `${window.location.origin}/posts/${postState._id}`,
    });
  };

  const handleDeletePost = async (postId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to delete the post.');
        return;
      }

      await axios.delete(`http://localhost:5000/pawpaw_test/posts/${postId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert('Post deleted successfully.');
    } catch (error) {
      console.error('Failed to delete post:', error);
    }
  };

  return (
    <Card
      key={postState._id}
      sx={{
        maxWidth: 800,
        margin: '20px auto',
        borderRadius: '16px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        padding: '16px',
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      {/* Post Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="subtitle1">
          {postState.userId.username} | {new Date(postState.createdAt).toLocaleDateString()} |{' '}
          {new Date(postState.createdAt).toLocaleTimeString()}
        </Typography>
        {currentUserId && post.userId && currentUserId === post.userId.toString() && (
          <DeleteButton onDelete={() => handleDeletePost(postState._id)} />
        )}
      </Box>

      {/* Post Content */}
      <Box sx={{ display: 'flex', marginTop: '16px' }}>
        {post.images && post.images.length > 0 && (
          <img
            src={`http://localhost:5000${post.images[0].url}`}
            alt="Post Thumbnail"
            style={{
              width: '120px',
              height: '120px',
              borderRadius: '12px',
              objectFit: 'cover',
              marginRight: '16px',
              cursor: 'pointer',
            }}
            onClick={() => window.open(`http://localhost:5000${post.images[0].url}`, '_blank')}
          />
        )}
        <Typography variant="body1" sx={{ flex: 1, alignSelf: 'center' }}>
          {postState.content}
        </Typography>
      </Box>

      {/* Actions */}
      <Box sx={{ display: 'flex', alignItems: 'center', gap: '8px', marginTop: '16px' }}>
        <LikeButton
          likedByUser={currentUserId ? postState.likes.includes(currentUserId) : false}
          onLike={handleLikePost}
          likeCount={postState.likes.length}
        />
        <IconButton aria-label="comment" onClick={toggleComments}>
          <ReviewsIcon />
        </IconButton>
        <IconButton aria-label="share" onClick={handleSharePost}>
          <IosShareIcon />
        </IconButton>
      </Box>

      {/* Comments Section */}
      {showComments && (
        <Box sx={{ marginTop: '16px' }}>
          <Comment postId={postState._id} />
        </Box>
      )}
    </Card>
  );
};

export default Post;
