import React, { useState, useEffect } from 'react';
import { Box, Fab, Dialog, DialogActions, DialogContent, DialogTitle, Button } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import CreatePost from '../components/CreatePost';
import Post from '../components/Post';
import axios from 'axios';

const Forum = () => {
  const [posts, setPosts] = useState([]);
  const [openCreatePost, setOpenCreatePost] = useState(false);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/pawpaw/all');
        if (response.data.success) {
          setPosts(response.data.posts);
        }
      } catch (error) {
        console.error('Failed to fetch posts:', error);
      }
    };

    fetchPosts();
  }, []);

  // Function for adding a new post
  const handleAddPost = (newPost) => {
    setPosts([newPost, ...posts]);
    setOpenCreatePost(false); // Close the CreatePost popup after adding
  };

  return (
    <Box
  sx={{
    padding: '20px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: '#F5F5F5',
    minHeight: '100vh',
  }}
>
  {/* CreatePost Section */}
  <Box
    sx={{
      width: '100%',
      maxWidth: '900px',
      backgroundColor: '#F2E96E',
      borderRadius: '8px',
      padding: '20px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
      mb: 4,
    }}
  >
    <CreatePost onAddPost={handleAddPost} />
  </Box>

  {/* Posts Section */}
  <Box
    sx={{
      width: '100%',
      maxWidth: '800px',
    }}
  >
    {posts.map((post, index) => (
      <Box
        key={post._id || index}
        sx={{
          backgroundColor: '#FFFFFF',
          borderRadius: '8px',
          padding: '15px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          mb: 3,
        }}
      >
        <Post post={post} />
      </Box>
    ))}
  </Box>

  {/* FAB Button */}
  <Fab
    color="primary"
    aria-label="add"
    onClick={() => setOpenCreatePost(true)}
    sx={{
      position: 'fixed',
      bottom: 20,
      right: 20,
      backgroundColor: '#BB7CDB',
      '&:hover': {
        backgroundColor: '#3CCCCD',
      },
    }}
  >
    <AddIcon />
  </Fab>

    {/* CreatePost Dialog */}
    <Dialog open={openCreatePost} onClose={() => setOpenCreatePost(false)}>
      <DialogTitle>Create a New Post</DialogTitle>
      <DialogContent>
        <CreatePost onAddPost={handleAddPost} />
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setOpenCreatePost(false)} color="primary">Cancel</Button>
      </DialogActions>
    </Dialog>
  </Box>
  );
};

export default Forum;
