import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    bio: '',
    petTypes: [],
    birthday: '',
    showEmail: false
  });

  // Fetch user profile on component mount
  useEffect(() => {
    axios.get('/pawpaw_test/profile', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } // Replace with your token management
    })
    .then(res => {
      setProfile(res.data);
      setFormData({
        bio: res.data.profile.bio,
        petTypes: res.data.profile.petTypes,
        birthday: res.data.profile.birthday,
        showEmail: res.data.profile.showEmail
      });
    })
    .catch(err => console.log(err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.put('/pawpaw_test/profile', formData, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(res => {
      setProfile(res.data);
      setIsEditing(false);
    })
    .catch(err => console.log(err));
  };

  return (
    <div>
      {profile && !isEditing ? (
        <div>
          <h2>{profile.username}'s Profile</h2>
          <p>Bio: {profile.profile.bio}</p>
          <p>Pet Types: {profile.profile.petTypes.join(', ')}</p>
          <p>Birthday: {new Date(profile.profile.birthday).toLocaleDateString()}</p>
          <p>Email: {profile.profile.showEmail ? profile.email : 'Hidden'}</p>
          <button onClick={() => setIsEditing(true)}>Edit Profile</button>
        </div>
      ) : (
        <div>
          <h2>Edit Profile</h2>
          <form onSubmit={handleSubmit}>
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Your bio"
            />
            <input
              type="text"
              name="petTypes"
              value={formData.petTypes}
              onChange={handleChange}
              placeholder="Pet Types (comma-separated)"
            />
            <input
              type="date"
              name="birthday"
              value={formData.birthday}
              onChange={handleChange}
            />
            <label>
              Show Email:
              <input
                type="checkbox"
                name="showEmail"
                checked={formData.showEmail}
                onChange={() => setFormData({ ...formData, showEmail: !formData.showEmail })}
              />
            </label>
            <button type="submit">Save</button>
          </form>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default Profile;
