import React, { useState } from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, IconButton, Tooltip } from '@mui/material';
import { Link } from 'react-router-dom'; 
import MenuIcon from '@mui/icons-material/Menu';
import HomeIcon from '@mui/icons-material/Home';
import AssignmentIndIcon from '@mui/icons-material/AssignmentInd';
import PetsIcon from '@mui/icons-material/Pets';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';

const Sidebar = () => {
  const [open, setOpen] = useState(true); // true for expanded, false for collapsed

  const toggleSidebar = () => {
    setOpen(!open);
  };

  return (
    <Drawer
      variant="permanent"
      open={open}
      sx={{
        '& .MuiDrawer-paper': {
          width: open ? 100 : 70, // Width when expanded and collapsed
          transition: 'width 0.3s',
          overflowX: 'hidden',
        },
      }}
    >
      <IconButton onClick={toggleSidebar} style={{ margin: '8px' }}>
        {open ? <ChevronLeftIcon /> : <MenuIcon />}
      </IconButton>
      <List>
        <Tooltip title="Home" placement="right" disableHoverListener={open}>
          <ListItem button component={Link} to="/">
            <ListItemIcon><HomeIcon /></ListItemIcon>
            {open && <ListItemText primary="Home" />}
          </ListItem>
        </Tooltip>

        <Tooltip title="Profile" placement="right" disableHoverListener={open}>
          <ListItem button component={Link} to="/profile">
            <ListItemIcon><AssignmentIndIcon /></ListItemIcon>
            {open && <ListItemText primary="Profile" />}
          </ListItem>
        </Tooltip>

        <Tooltip title="Forum" placement="right" disableHoverListener={open}>
          <ListItem button component={Link} to="/forum">
            <ListItemIcon><PetsIcon /></ListItemIcon>
            {open && <ListItemText primary="Forum" />}
          </ListItem>
        </Tooltip>

        <Tooltip title="Blog" placement="right" disableHoverListener={open}>
          <ListItem button component={Link} to="/blog">
            <ListItemIcon><LibraryBooksIcon /></ListItemIcon>
            {open && <ListItemText primary="Blog" />}
          </ListItem>
        </Tooltip>
      </List>
    </Drawer>
  );
};

export default Sidebar;
