import React, { useState } from 'react';
import { Card, CardContent, CardActions, Button, TextField, Typography, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ImageUpload from './ImageUpload';

const CreatePost = ({ onAddPost }) => {
  const [content, setContent] = useState('');
  const [images, setImages] = useState([]);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // ตรวจสอบว่ามี token หรือไม่
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to create a post');
      navigate('/login');
      return; // ยกเลิกการทำงานหากผู้ใช้ไม่ได้ login
    }

    try {
      // สร้างคำขอ POST ไปยัง Backend เพื่อสร้างโพสต์ใหม่
      const formData = new FormData();
      formData.append('content', content);
      images.forEach((image, index) => {
        formData.append(`images[${index}]`, image);
      });

      const response = await axios.post('http://localhost:5000/pawpaw_test/create', 
        formData, 
        { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' } } // แนบ token สำหรับยืนยันตัวตน
      );

      // ตรวจสอบผลลัพธ์จาก Backend
      if (response.data.success) {
        const newPost = {
          id: response.data.post._id,
          username: response.data.post.username,
          content: response.data.post.content,
          createdAt: response.data.post.createdAt,
          images: response.data.post.images,
          likes: response.data.post.likes,
        };
        onAddPost(newPost); // ส่งโพสต์ใหม่ไปยัง Forum
        setContent('');
        setImages([]);
      }
    } catch (error) {
      console.error('Failed to create post', error);
    }
  };

  return (
    <Card sx={{ maxWidth: 600, margin: '20px auto' }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          Create a Post
        </Typography>
        <TextField
          label="What's on your mind?"
          multiline
          rows={4}
          value={content}
          onChange={(e) => setContent(e.target.value)}
          fullWidth
          sx={{ marginBottom: '1rem' }}
        />
        {/* Image upload area */}
        <Box sx={{ marginBottom: '1rem' }}>
          <ImageUpload onImagesChange={setImages} />
          {images.length > 0 && (
            <Typography variant="body2" color="textSecondary">
              {images.length} image(s) selected
            </Typography>
          )}
        </Box>
      </CardContent>
      <CardActions>
        <Button variant="contained" color="primary" onClick={handleSubmit}>
          Post
        </Button>
      </CardActions>
    </Card>
  );
};

export default CreatePost;
