import React, { useState } from 'react';
import { Card, CardContent, CardActions, IconButton, Typography, TextField, Button } from '@mui/material';
import Grid from '@mui/material/Grid';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import axios from 'axios';

const Post = ({ post, onLike, likedByUser, onComment }) => {
  const [showCommentBox, setShowCommentBox] = useState(false);
  const [comment, setComment] = useState('');

  const handleCommentClick = () => {
    setShowCommentBox(!showCommentBox);
  };

  const handleCommentSubmit = async () => {
    if (comment.trim() !== '') {
      try {
        // ดึง token จาก localStorage
        const token = localStorage.getItem('token');
        if (!token) {
          console.error('User not authenticated');
          return; // หากไม่มี token ให้หยุดการทำงาน
        }
  
        const response = await axios.post(`http://localhost:5000/pawpaw_test/comment/${post._id}`, {
          content: comment
        }, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
  
        if (response.data.success) {
          onComment(post._id, response.data.comment);
          setComment('');
        }
      } catch (error) {
        console.error('Failed to create comment:', error);
      }
    }
  };
  

  return (
    <Card key={post._id} sx={{ maxWidth: 600, margin: '20px auto' }}>
      <CardContent>
        <Typography variant="subtitle1" color="textSecondary">
          {post.username} • {new Date(post.createdAt).toLocaleDateString()}
        </Typography>
        <Typography variant="body1" sx={{ marginTop: '1rem' }}>
          {post.content}
        </Typography>
  
        {post.images && post.images.length > 0 && (
          <Grid container spacing={1} sx={{ marginTop: '1rem' }}>
            {post.images.map((image, index) => (
              <Grid item xs={6} key={index}>
                <img src={image.url} alt="" style={{ width: '100%', borderRadius: '4px' }} />
              </Grid>
            ))}
          </Grid>
        )}
      </CardContent>
      <CardActions>
        <IconButton aria-label="like" onClick={onLike}>
          {likedByUser ? <FavoriteIcon /> : <FavoriteBorderIcon />}
          <Typography variant="body2" sx={{ marginLeft: 0.5 }}>{post.likes.length}</Typography>
        </IconButton>
        <IconButton aria-label="comment" onClick={handleCommentClick}>
          <ReviewsIcon />
        </IconButton>
        
        {showCommentBox && (
          <>
            <TextField
              label="Write a comment..."
              variant="outlined"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              size="small"
              sx={{ marginLeft: '1rem', flex: 1 }} // ทำให้ช่องคอมเมนต์ใหญ่ขึ้น และจัดให้อยู่ด้านข้างไอคอนคอมเมนต์
            />
            <Button variant="contained" color="primary" onClick={handleCommentSubmit} sx={{ marginLeft: '0.5rem' }}>
              Comment
            </Button>
          </>
        )}
  
        <IconButton aria-label="share">
          <IosShareIcon />
        </IconButton>
      </CardActions>
    </Card>
  );
};

export default Post;
