import React, { useState } from 'react';

const Profile = () => {
  const user = {
    name: "Akira Arisara",
    profession: "Student",
    bio: "Passionate about design, technology, and creativity.",
    location: "Bangkok, Thailand",
    email: "charlesdeo@example.com",
    phone: "+1 234 567 890",
    image: "/88.jpg",  // เปลี่ยนเป็นที่อยู่ของรูปภาพ
    posts: [
      { id: 1, title: "New Blazer Collection", image: "https://via.placeholder.com/600", likes: 1400, comments: 100 },
      { id: 2, title: "Latest Fashion Trends", image: "https://via.placeholder.com/600", likes: 1500, comments: 120 },
    ]
  };

  const [isFollowing, setIsFollowing] = useState(false);

  const handleFollow = () => {
    setIsFollowing(!isFollowing); // สลับสถานะการติดตาม
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <div style={styles.profileHeader}>
          <img src={user.image} alt="Profile" style={styles.profileImage} /> {/* ใส่รูปโปรไฟล์ */}
          <h2>{user.name}</h2>
          <p>{user.profession}</p>
          {/* ปุ่มติดตาม */}
          <button style={styles.followButton} onClick={handleFollow}>
            {isFollowing ? 'Unfollow' : 'Follow'}
          </button>
        </div>
        <div style={styles.aboutSection}>
          <h3>About</h3>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone}</p>
          <p><strong>Location:</strong> {user.location}</p>
        </div>
      </div>

      {/* Posts Section */}
      <div style={styles.posts}>
        <h3>Posts</h3>
        {user.posts.map(post => (
          <div key={post.id} style={styles.post}>
            <h4>{post.title}</h4>
            <img src={post.image} alt="Post" style={styles.postImage} />
            <div style={styles.postStats}>
              <span>{post.likes} Likes</span>
              <span>{post.comments} Comments</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileHeader: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  profileImage: {
    borderRadius: '50%',
    width: '120px',
    height: '120px',
    objectFit: 'cover',
  },
  followButton: {
    marginTop: '10px',
    padding: '10px',
    backgroundColor: '#6c63ff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  aboutSection: {
    marginTop: '20px',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
  post: {
    marginBottom: '20px',
    backgroundColor: '#fff',
    borderRadius: '8px',
    padding: '15px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  },
  postImage: {
    width: '100%',
    height: 'auto',
    borderRadius: '8px',
    marginTop: '10px',
  },
  postStats: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '10px',
  },
};

export default Profile;
