import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Post from '../components/Post';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');
  
    if (!token || !userId) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }
  
    // ดึงข้อมูลโปรไฟล์จาก API
    axios
      .get('http://localhost:5000/pawpaw_test/profile', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setUser(response.data);  // กำหนดข้อมูลผู้ใช้
        console.log('User Data:', response.data);
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
        alert('Failed to load user profile. Please try again.');
        navigate('/login');
      });
  
    // ดึงโพสต์ของผู้ใช้จาก API
    axios
      .get(`http://localhost:5000/pawpaw_test/posts/user/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setPosts(response.data.posts); // ตั้งค่าโพสต์
        console.log('Posts:', response.data.posts);
      })
      .catch((error) => {
        console.error('Error fetching user posts:', error);
      });
  }, [navigate]);

  if (!user) {
    return null;  // ถ้า user ยังไม่มีข้อมูลหรือกำลังโหลด ให้ไม่แสดงอะไร
  }

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        <div style={styles.profileHeader}>
          <h2>{user.username}</h2>
        </div>
        <div style={styles.aboutSection}>
          <h3>About</h3>
          {user.showEmail && <p><strong>Email:</strong> {user.email}</p>}
        </div>
      </div>

      <div style={styles.posts}>
        <h3>Posts</h3>
        {posts.length > 0 ? (
          posts.map((post) => (
            <Post key={post._id} post={post} />
          ))
        ) : (
          <p>No posts available. Try posting something about your pet!</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileHeader: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  aboutSection: {
    marginTop: '20px',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
};

export default Profile;
