import React, { useState } from 'react';
import { Card, CardContent, CardActions, Button, TextField, Typography } from '@mui/material';
import axios from 'axios';

const CreatePost = ({ onPostCreated }) => {
  const [content, setContent] = useState('');
  const [images, setImages] = useState([]);
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/pawpaw_test/posts/create', 
        { content, images }, 
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      if (response.data.success) {
        onPostCreated();
        setContent('');
        setImages([]);
      }
    } catch (error) {
      console.error('Failed to create post', error);
    }
};

  return (
    <Box
      sx={{
        display: 'flex',
        alignItems: 'center',
        padding: '16px',
        backgroundColor: '#2E2E2E',
        borderRadius: '8px',
        marginBottom: '16px'
      }}
    >
      <Avatar sx={{ marginRight: '16px' }} />
      <TextField
        placeholder="Let’s share what’s going on your mind..."
        variant="outlined"
        fullWidth
        sx={{ backgroundColor: '#fff', borderRadius: '8px' }}
      />
      <Button variant="contained" color="primary" sx={{ marginLeft: '16px', backgroundColor: '#F97316' }}>
        Create Post
      </Button>
    </Box>
  );
};

export default CreatePost;
