import React, { useState, useEffect } from 'react';
import { Box, Typography, IconButton, TextField, Button, Card } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import LikeButton from './LikeButton'; // สมมติว่าคุณมีปุ่มนี้
import DeleteButton from './DeleteButton'; // สมมติว่าคุณมีปุ่มนี้
import Reply from './Reply';
import axios from 'axios';

const Comment = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [showCommentBox, setShowCommentBox] = useState(false); // state สำหรับแสดง/ซ่อน textarea

  useEffect(() => {
    fetchComments();
  }, []);

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleAddComment = async () => {
    if (newComment.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
        { content: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setComments([response.data.comment, ...comments]);
      setNewComment('');
      setShowCommentBox(false); // ซ่อน textarea หลังจากโพสต์สำเร็จ
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  return (
    <Card
      sx={{
        maxWidth: 800,
        margin: '20px auto',
        borderRadius: '16px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        padding: '16px',
      }}
    >

      {/* Comment Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
          Comments
        </Typography>
        <IconButton
          aria-label="toggle-comment-box"
          onClick={() => setShowCommentBox(!showCommentBox)}
        >
          <ReviewsIcon />
        </IconButton>
      </Box>

      {/* Toggle Comment Input */}
      {showCommentBox && (
        <Box sx={{ marginTop: '16px' }}>
          <TextField
            placeholder="Write a comment..."
            variant="outlined"
            fullWidth
            multiline
            rows={3}
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            sx={{ marginBottom: '8px' }}
          />
          <Button variant="contained" color="primary" onClick={handleAddComment}>
            Post Comment
          </Button>
        </Box>
      )}

      {/* Display Comments */}
      {comments.map((comment) => (
        <Box
          key={comment._id}
          sx={{
            marginTop: '16px',
            padding: '8px',
            border: '1px solid #e0e0e0',
            borderRadius: '8px',
          }}
        >
          <Typography variant="subtitle2" color="textSecondary">
            {comment.userId?.username || 'Unknown User'} •{' '}
            {new Date(comment.createdAt).toLocaleDateString()} •{' '}
            {new Date(comment.createdAt).toLocaleTimeString()}
          </Typography>
          <Typography variant="body1" sx={{ margin: '8px 0' }}>
            {comment.content}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <LikeButton
              likedByUser={false} // คุณสามารถใส่ logic เพิ่มเติมได้
              onLike={() => console.log('Liked comment!')}
              likeCount={comment.likes.length}
            />
            <IconButton>
              <ReviewsIcon />
            </IconButton>
          </Box>

          {/* Render Nested Replies */}
          {comment.replies && comment.replies.length > 0 && (
            <Box sx={{ marginLeft: '16px', marginTop: '8px' }}>
              <Reply comment={comment} postId={postId} fetchComments={fetchComments} />
            </Box>
          )}
        </Box>
      ))}
    </Card>
  );
};

export default Comment;
