import React, { useState } from 'react';
import { Box, TextField, Button, Card, CardContent, CardMedia, Typography } from '@mui/material';

const Forum = () => {
  const [posts, setPosts] = useState([]); // State to hold posts
  const [postContent, setPostContent] = useState(''); // State for new post content
  const [images, setImages] = useState([]); // State for images

  const handlePostSubmit = (e) => {
    e.preventDefault();
    if (postContent || images.length > 0) {
      const newPost = {
        id: posts.length + 1,
        content: postContent,
        images: images,
        username: 'User', // Replace with actual username
      };
      setPosts([...posts, newPost]); // Add new post to the list
      setPostContent(''); // Clear input field
      setImages([]); // Clear image state
    }
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files).slice(0, 4); // Limit to 4 images
    setImages(files); // Update images state
  };

  return (
    <div>
      <h1>Forum</h1>
      <form onSubmit={handlePostSubmit}>
        <Box mb={2}>
          <TextField
            label="What's on your mind?"
            variant="outlined"
            value={postContent}
            onChange={(e) => setPostContent(e.target.value)}
            multiline
            rows={4}
            fullWidth
            required
          />
        </Box>
        <input type="file" accept="image/*" multiple onChange={handleImageChange} /> {/* Input for images */}
        <Button type="submit" variant="contained" color="primary">
          Post
        </Button>
      </form>

      <div style={{ marginTop: '20px' }}>
        <h2>Posts</h2>
        {posts.map((post) => (
          <Card key={post.id} style={{ display: 'flex', margin: '10px 0', borderRadius: '15px' }}>
            {post.images.length > 0 && (
              <div style={{ display: 'flex', flexDirection: 'column' }}>
                {post.images.slice(0, 4).map((image, index) => (
                  <CardMedia
                    key={index}
                    component="img"
                    image={URL.createObjectURL(image)} // Display image from input
                    alt={`Post image ${index + 1}`}
                    style={{ width: '100px', height: '100px', borderRadius: '10px' }} // Adjust size and radius
                  />
                ))}
              </div>
            )}
            <CardContent style={{ flex: 1 }}>
              <Typography variant="h6" component="div">
                {post.username}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {post.content}
              </Typography>
              <div style={{ marginTop: '10px' }}>
                <Button size="small">Comment</Button>
                <Button size="small">‚ù§Ô∏è Like</Button>
                <Button size="small">üîó Share</Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Forum;
