import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import { Routes, Route } from 'react-router-dom';
import Home from './components/Home';
import Profile from './components/Profile';
import Forum from './components/Forum';
import Blog from './components/Blog';
import Login from './components/Login';
import Register from './components/Register';
import Header from './components/Header'; // Import Header
import { UserProvider } from './components/UserContext'; // Import UserProvider

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => setIsLoggedIn(true);
  const handleLogout = () => setIsLoggedIn(false);
  return (
    <div style={{ display: 'flex' }}>
      <Sidebar isLoggedIn={isLoggedIn} onLogout={handleLogout} />
      <main style={{ flexGrow: 1, padding: '24px', marginTop: '60px' }}>
      {user && (
          <div style={{ position: 'absolute', top: '10px', right: '10px', display: 'flex', alignItems: 'center' }}>
            <img src={user.profilePicture} alt="Profile" style={{ width: '40px', height: '40px', borderRadius: '50%', marginRight: '10px' }} />
            <span>{user.username}</span>
          </div>
      )}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/forum" element={<Forum />} />
          <Route path="/blog" element={<Blog />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </main>
    </div>
  );
};

export default App;
