import React from 'react';
import { Drawer, List, ListItem, ListItemText, IconButton } from '@mui/material';
import { Link } from 'react-router-dom'; 
import MenuIcon from '@mui/icons-material/Menu';
import HomeIcon from '@mui/icons-material/Home';
import AssignmentIndIcon from '@mui/icons-material/AssignmentInd';
import PetsIcon from '@mui/icons-material/Pets';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';

const Sidebar = ({ open, toggleSidebar }) => {
  return (
    <Drawer
      variant="persistent"
      anchor="left"
      open={open}
      sx={{
        '& .MuiDrawer-paper': {
          width: open ? 240 : 70, // Width when expanded and collapsed
          transition: 'width 0.3s',
        },
      }}
    >
      <IconButton onClick={toggleSidebar} style={{ margin: '8px' }}>
        {open ? <ChevronLeftIcon /> : <MenuIcon />}
      </IconButton>
      <List>
        <ListItem button component={Link} to="/">
          <HomeIcon />
          {open && <ListItemText primary="Home" />}
        </ListItem>
        <ListItem button component={Link} to="/profile">
          <AssignmentIndIcon />
          {open && <ListItemText primary="Profile" />}
        </ListItem>
        <ListItem button component={Link} to="/forum">
          <PetsIcon />
          {open && <ListItemText primary="Forum" />}
        </ListItem>
        <ListItem button component={Link} to="/blog">
          <LibraryBooksIcon />
          {open && <ListItemText primary="Blog" />}
        </ListItem>
      </List>
    </Drawer>
  );
};

export default Sidebar;
