import React from 'react';
import { Card, CardContent, Typography, Button, CardMedia, Box } from '@mui/material';
import ReviewsTwoToneIcon from '@mui/icons-material/ReviewsTwoTone';
import FavoriteTwoToneIcon from '@mui/icons-material/FavoriteTwoTone';
import FavoriteIcon from '@mui/icons-material/Favorite';
import IosShareIcon from '@mui/icons-material/IosShare';

const Post = ({ post }) => {
  return (
    <Card style={{ margin: '10px 0', borderRadius: '15px', padding: '10px', display: 'flex', backgroundColor: '#FFFFFF' }}>
      {post.images && post.images.length > 0 && (
        <div style={{ display: 'flex', flexDirection: 'column', gap: '5px', paddingRight: '10px' }}>
          {post.images.slice(0, 4).map((image, index) => (
            <CardMedia
              key={index}
              component="img"
              image={image.url} // Assuming the image object contains a URL
              alt={`Post image ${index + 1}`}
              style={{ width: '80px', height: '80px', borderRadius: '8px' }}
            />
          ))}
        </div>
      )}
      <CardContent style={{ flex: 1 }}>
        <Typography variant="h6" component="div" style={{ fontWeight: 'bold', color: '#333' }}>
          {post.username}
        </Typography>
        <Typography variant="body2" style={{ maxHeight: '100px', overflow: 'hidden', textOverflow: 'ellipsis', color: '#666' }}>
          {post.content.length > 100 ? `${post.content.substring(0, 100)}...` : post.content}
          {post.content.length > 100 && (
            <span style={{ cursor: 'pointer', color: '#1A73E8' }} onClick={() => alert(post.content)}>
              Read More
            </span>
          )}
        </Typography>
        <Box display="flex" justifyContent="space-between" alignItems="center" style={{ marginTop: '10px' }}>
          <Button size="small" startIcon={<ReviewsTwoToneIcon />} style={{ color: '#1A73E8' }}>Comment</Button>
          <Button size="small" startIcon={post.liked ? <FavoriteIcon style={{ color: 'red' }} /> : <FavoriteTwoToneIcon />} onClick={() => {/* Handle like */}} style={{ color: post.liked ? 'red' : '#1A73E8' }}>
            {post.liked ? 'Liked' : 'Like'}
          </Button>
          <Button size="small" startIcon={<IosShareIcon />} style={{ color: '#1A73E8' }}>Share</Button>
        </Box>
      </CardContent>
    </Card>
  );
};

export default Post;
