//
import React, { useState } from 'react';
import { TextField, Button, Typography, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';

const Comment = ({ postId, comments, onAddComment }) => {  // Use props as parameters here
  const [comment, setComment] = useState('');
  const [showCommentField, setShowCommentField] = useState(false);

  const handleCommentSubmit = async () => {
    if (comment.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to comment.');
          return;
        }

        // Send comment to backend
        const response = await axios.post(`http://localhost:5000/pawpaw_test/posts/${postId}/comment`, {
          content: comment
        }, {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (response.data.success) {
          onAddComment(response.data.comment);  // Use the onAddComment prop to add the new comment
          setComment('');  // Clear the comment input field
        }
      } catch (error) {
        console.error('Failed to create comment:', error);
      }
    }
  };

  const handleReplySubmit = async (commentId) => {
    const replyContent = replyInputs[commentId];
    if (replyContent && replyContent.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to reply.');
          return;
        }
  
        // ส่งการตอบกลับไปยัง backend
        const response = await axios.post(
          `http://localhost:5000/pawpaw_test/comments/${commentId}/reply`,
          { content: replyContent },
          { headers: { Authorization: `Bearer ${token}` } }
        );
  
        if (response.data.success) {
          // เพิ่มการตอบกลับใน state
          setReplies((prevReplies) => ({
            ...prevReplies,
            [commentId]: [...(prevReplies[commentId] || []), response.data.reply]
          }));
          setReplyInputs((prevState) => ({
            ...prevState,
            [commentId]: '', // ล้างช่อง input ของการตอบกลับ
          }));
        }
      } catch (error) {
        console.error('Failed to create reply:', error);
      }
    }
  };

  return (
    <div>
    {comments.map((comment) => (
      <div key={comment._id} style={{ marginTop: '1rem', padding: '10px', border: '1px solid #e0e0e0', borderRadius: '5px' }}>
        <Typography variant="body2" sx={{ marginBottom: '0.5rem' }}>
          {comment.username}: {comment.content}
        </Typography>
        <div>
          {/* Replace with LikeButton component */}
          <LikeButton 
            likedByUser={comment.likedByUser} 
            onLike={() => {
              // Add your like logic here or pass props accordingly
              console.log(`Liked comment with ID: ${comment._id}`);
            }} 
            likeCount={comment.likes ? comment.likes.length : 0}
          />

          {/* Comment Icon to toggle comment input field */}
          <IconButton 
            aria-label="comment" 
            onClick={() => setShowCommentField(prevState => !prevState)}
          >
            <ReviewsIcon />
          </IconButton>

          {/* Share Icon */}
          <IconButton aria-label="share">
            <IosShareIcon />
          </IconButton>

          {/* Show comment input field when icon is clicked */}
          {showCommentField && (
            <div style={{ marginTop: '1rem' }}>
              <TextField
                label="Write a comment..."
                variant="outlined"
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                size="small"
                fullWidth
                sx={{ marginBottom: '0.5rem' }}
              />
              <Button variant="contained" color="primary" onClick={handleCommentSubmit}>
                Comment
              </Button>
            </div>
          )}
        </div>
      </div>
    ))}
  </div>
  );
};

export default Comment;
