import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import Home from './components/Home';
import Profile from './components/Profile';
import Forum from './components/Forum';
import Blog from './components/Blog';
import Login from './components/Login';
import Register from './components/Register';
import Sidebar from './components/Sidebar';
//import Header from './components/Header';
import Layout from './components/Layout';
import { Box } from '@mui/material';

const App = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true); // State to manage sidebar

  const toggleSidebar = () => {
    setSidebarOpen(prevState => !prevState); // Toggle sidebar open/closed
  };

  return (
      <Box sx={{ display: 'flex' }}>
        <Sidebar open={sidebarOpen} toggleSidebar={toggleSidebar} />
        <Box
          sx={{
            flex: 1,
            padding: '20px',
            marginLeft: sidebarOpen ? '125px' : '70px', // Adjust margin based on sidebar state
            transition: 'margin-left 0.3s',
          }}
        >
          {/*<Header />*/}
          <Layout>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/forum" element={<Forum />} />
            <Route path="/blog" element={<Blog />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
          </Routes>
          </Layout>
        </Box>
      </Box>
  );
};

export default App;
