import React, { useState, memo } from 'react';
import { TextField, Button, Typography, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';

const Comment = ({ postId, comments }) => {
  const [replyInputs, setReplyInputs] = useState({}); // Store input for each reply
  const [showReplyFields, setShowReplyFields] = useState({}); // Show/hide each reply input

  // Handle submitting a reply to a comment
  const handleReplySubmit = async (commentId) => {
    const replyContent = replyInputs[commentId];
    if (replyContent && replyContent.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to reply.');
          return;
        }

        // Sending reply to the backend
        const response = await axios.post(
          `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
          {
            content: replyContent,
            parentCommentId: commentId, // Specify parentCommentId to indicate it's a reply
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (response.data.success) {
          // Add the reply to the correct comment in the comments list
          onAddComment(response.data.comment);
          
          // Close the reply field
          setShowReplyFields((prevState) => ({
            ...prevState,
            [commentId]: false,
          }));
          
          // Clear the reply input field
          setReplyInputs((prevState) => ({
            ...prevState,
            [commentId]: '',
          }));
        }
      } catch (error) {
        console.error('Failed to create reply:', error);
      }
    }
  };

  // Adding a new comment or reply
  const onAddComment = (newComment) => {
    setComments((prevComments) => {
      if (newComment.parentCommentId) {
        // If the new comment is a reply, find the parent and add it to its replies
        return prevComments.map((comment) => {
          if (comment._id === newComment.parentCommentId) {
            return {
              ...comment,
              replies: [...(comment.replies || []), newComment],
            };
          }
          return comment;
        });
      } else {
        // Otherwise, it's a top-level comment
        return [...prevComments, newComment];
      }
    });
  };


  // Memoized comment card component
  const CommentCard = memo(({ comment }) => {
    return (
      <div
        style={{
          marginTop: '1rem',
          padding: '10px',
          border: '1px solid #e0e0e0',
          borderRadius: '5px',
        }}
      >
        {/* Username, Date, and Time */}
        <Typography variant="subtitle2" color="textSecondary">
          {comment.userId?.username ? comment.userId.username : 'Unknown'} •{' '}
          {new Date(comment.createdAt).toLocaleDateString()} •{' '}
          {new Date(comment.createdAt).toLocaleTimeString()}
        </Typography>

        {/* Content */}
        <Typography variant="body1" sx={{ marginBottom: '0.5rem' }}>
          {comment.content}
        </Typography>

        {/* Actions */}
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <LikeButton
            likedByUser={comment.likedByUser}
            onLike={() => {
              console.log(`Liked comment with ID: ${comment._id}`);
            }}
            likeCount={comment.likes ? comment.likes.length : 0}
          />
          <IconButton
            aria-label="reply"
            onClick={() => toggleReplyField(comment._id)}
            style={{ marginLeft: '0.5rem' }}
          >
            <ReviewsIcon />
          </IconButton>
          <IconButton aria-label="share" style={{ marginLeft: '0.5rem' }}>
            <IosShareIcon />
          </IconButton>
        </div>

        {/* Reply input field */}
        {showReplyFields[comment._id] && (
          <div
            style={{
              display: 'flex',
              alignItems: 'center',
              marginLeft: '1rem',
              marginTop: '1rem',
            }}
          >
            <TextField
              label="Write a reply..."
              variant="outlined"
              value={replyInputs[comment._id] || ''}
              onChange={(e) =>
                setReplyInputs((prevState) => ({
                  ...prevState,
                  [comment._id]: e.target.value,
                }))
              }
              size="small"
              sx={{ flexGrow: 1, marginRight: '0.5rem' }}
              autoFocus // เพิ่ม autoFocus เพื่อให้ช่องมีโฟกัสเมื่อแสดง
            />
            <Button
              variant="contained"
              color="primary"
              onClick={() => handleReplySubmit(comment._id)}
            >
              Reply
            </Button>
          </div>
        )}

        {/* Display replies */}
        {comment.replies && comment.replies.length > 0 && (
          <div style={{ marginLeft: '2rem', marginTop: '1rem' }}>
            {comment.replies.map((reply) => (
              <CommentCard key={reply._id} comment={reply} />
            ))}
          </div>
        )}
      </div>
    );
  });

  return (
    <div>
      {comments.map((comment) => (
        <React.Fragment key={comment._id}>
          <CommentCard comment={comment} />
        </React.Fragment>
      ))}
    </div>
  );
};

export default Comment;
