import React, { useState, useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import '../Home.css';

import slide1 from '../images/40.jpg';
import slide2 from '../images/50.jpg';
import slide3 from '../images/60.jpg';
import blogImage1 from '../images/Blog1.jpg';
import blogImage2 from '../images/Blog2.jpg';
import blogImage3 from '../images/Blog3.jpg';
import blogImage4 from '../images/Blog4.jpg';

import newBlogImage1 from '../images/newBlog1.jpg';
import newBlogImage2 from '../images/newBlog2.jpg';
import newBlogImage3 from '../images/newBlog3.jpg';
import newBlogImage4 from '../images/newBlog4.jpg';

// ข้อมูลบล็อกใหม่ที่จะแสดงในกล่องข้อความ
const newBlogItems = [
  { id: 1, title: "รักน้องต้องห้ามทำ! ทำไมถึงห้ามไม่ให้สัตว์เลี้ยงกินอาหารคน", image: newBlogImage1, content: "", link: "https://www.ofm.co.th/blog/toxic-food-for-dogs/" },
  { id: 2, title: "โรงพยาบาลสัตว์ที่คัดสรรมาในปี 2024", image: newBlogImage2, content: "", link: "https://www.livinginsider.com/inside_topic/9960/10-%E0%B9%82%E0%B8%A3%E0%B8%87%E0%B8%9E%E0%B8%A2%E0%B8%B2%E0%B8%9A%E0%B8%B2%E0%B8%A5%E0%B8%AA%E0%B8%B1%E0%B8%95%E0%B8%A7%E0%B9%8C%E0%B8%97%E0%B8%B5%E0%B9%88%E0%B8%94%E0%B8%B5%E0%B8%97%E0%B8%B5%E0%B9%88%E0%B8%AA%E0%B8%B8%E0%B8%94%E0%B9%83%E0%B8%99%E0%B8%81%E0%B8%A3%E0%B8%B8%E0%B8%87%E0%B9%80%E0%B8%97%E0%B8%9E.html" },
  { id: 3, title: "สัตว์อะไรมาในฤดูฝน?", image: newBlogImage3, content: "", link: "https://www.wellcome-evolution.com/content/6681/insects-and-dangerous-animals-that-come-with-the-rainy-season" },
  { id: 4, title: "ดูแลสุนัขสูงวัยยังไงดี...", image: newBlogImage4, content: "", link: "https://www.royalcanin.com/th/dogs/health-and-wellbeing/your-dogs-end-of-life-care" },
];

// ข้อมูลบล็อกสำหรับการค้นหา
const blogItems = [
  { id: 1, title: "", image: blogImage1, content: "A balanced diet is crucial for your pet's health.", link: "https://pets.baanlaesuan.com/157867/pet-food/fruits-for-dogs" },
  { id: 2, title: "", image: blogImage2, content: "Discover must-have pet care products.", link: "https://www.sanook.com/horoscope/235505/" },
  { id: 3, title: "", image: blogImage3, content: "Choosing the perfect pet for your family.", link: "https://www.petexpothailand.net/page-exhibitor-application.php" },
  { id: 4, title: "", image: blogImage4, content: "Learn how to bond with your pet.", link: "https://hdmall.co.th/blog" },
];

const Home = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [currentBlogIndex, setCurrentBlogIndex] = useState(0);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  // ฟังก์ชันเลื่อนบล็อกในกล่องข้อความอัตโนมัติ
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentBlogIndex((prevIndex) => (prevIndex + 1) % newBlogItems.length);
    }, 3000); // เปลี่ยนทุก ๆ 3 วินาที

    return () => clearInterval(interval); // ล้าง interval เมื่อ component ออกจากหน้าจอ
  }, []);

  // ฟังก์ชันสำหรับเลื่อนไปบล็อกถัดไป
  const handleNext = () => {
    setCurrentBlogIndex((prevIndex) => (prevIndex + 1) % newBlogItems.length);
  };

  // ฟังก์ชันสำหรับเลื่อนไปบล็อกก่อนหน้า
  const handlePrevious = () => {
    setCurrentBlogIndex((prevIndex) => 
      (prevIndex - 1 + newBlogItems.length) % newBlogItems.length
    );
  };

  // กรองบล็อกที่ต้องการแสดงตามการค้นหา
  const filteredItems = blogItems.filter(item =>
    item.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="home">
      {/* ช่องค้นหา */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search ..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-input"
        />
      </div>

      {/* สไลด์โชว์ */}
      <Slider {...settings}>
        <div>
          <img src={slide1} alt="Slide 1" />
        </div>
        <div>
          <img src={slide2} alt="Slide 2" />
        </div>
        <div>
          <img src={slide3} alt="Slide 3" />
        </div>
      </Slider>

      {/* แสดงบล็อกตามการค้นหาที่กรอง */}
      <div className="blog-list">
        <h2></h2>
        <div className="blogs">
          {filteredItems.length > 0 ? (
            filteredItems.map(item => (
              <div key={item.id} className="blog-item">
                <a
                  href={item.link}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={item.image} alt={item.title} />
                  <div className="title">{item.title}</div>
                </a>
              </div>
            ))
          ) : (
            <p>No blogs found</p>
          )}
        </div>
      </div>

      {/* กล่องข้อความที่แสดงบล็อกใหม่พร้อมปุ่มเลื่อน */}
      <div className="scrollable-message-box">
        <h3>สาระสัตว์เลี้ยง</h3>
        <div className="blog-box">
        {newBlogItems.map((item, index) => (
      <div key={item.id} className="blog-item">
        <img src={item.image} alt={item.title} className="selected-blog-image" />
        <div className="blog-title">{item.title}</div>
        <a href={item.link} target="_blank" rel="noopener noreferrer">Read more...</a>
      </div>
    ))}
  </div>
       
      </div>
    </div>
  );
};

export default Home;
