import React, { useState, useEffect } from 'react';
import { Box, Typography, Fab } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import Post from '../components/Post'; // Import Post component

const Forum = () => {
  const [posts, setPosts] = useState([]); // State to hold posts

  useEffect(() => {
    // Fetch posts from the backend
    // Sample data for now
    const samplePosts = [
      { id: 1, username: 'User1', content: 'This is a sample post', time: 'April 6, 3:00PM' },
      { id: 2, username: 'User2', content: 'Another post example', time: 'April 6, 4:30PM' },
    ];
    setPosts(samplePosts);
  }, []);

  const handleCreatePost = () => {
    // Logic to handle new post creation
    alert('Create post modal');
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <Typography variant="h4" sx={{ mb: 2 }}>
        Forum
      </Typography>
      <Box sx={{ width: '100%', maxWidth: '800px' }}>
        {posts.map((post) => (
          <Post key={post.id} post={post} />
        ))}
      </Box>
      <Fab
        color="primary"
        aria-label="add"
        onClick={handleCreatePost}
        sx={{ position: 'fixed', bottom: 20, right: 20 }}
      >
        <AddIcon />
      </Fab>
    </Box>
  );
};

export default Forum;
