import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, Box, Typography } from '@mui/material';
import Reply from './Reply'; // ใช้ Reply.js สำหรับการแสดงการตอบกลับ

const Comment = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    fetchComments();
  }, [postId]);

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleAddComment = async () => {
    if (newComment.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
        { content: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setComments([response.data.comment, ...comments]);
      setNewComment('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  return (
    <Box sx={{ padding: '16px' }}>
      {/* Input for adding a new comment */}
      <Box sx={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
        <TextField
          label="Write a comment..."
          variant="outlined"
          size="small"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          sx={{ flex: 1 }}
        />
        <Button onClick={handleAddComment} variant="contained" color="primary">
          Comment
        </Button>
      </Box>

      {/* Display the list of comments */}
      {comments.map((comment) => (
        <Box key={comment._id} sx={{ marginBottom: '12px' }}>
          <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
            {comment.userId.username}
          </Typography>
          <Typography variant="body2" sx={{ marginTop: '4px' }}>
            {comment.content}
          </Typography>

          {/* Display replies */}
          <Reply comment={comment} postId={postId} fetchComments={fetchComments} />
        </Box>
      ))}
    </Box>
  );
};

export default Comment;
