import React, { useState } from 'react';
import { Card, CardContent, CardActions, IconButton, Typography, TextField, Button } from '@mui/material';
import Grid from '@mui/material/Grid';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import axios from 'axios';

const Post = ({ post, onLike }) => {
  const [newComment, setNewComment] = useState('');
  const [comments, setComments] = useState(post.comments || []); // ถ้ามี comments มาใน post ให้ใช้

  // ฟังก์ชันสำหรับเพิ่มคอมเม้นท์ใหม่
  const handleAddComment = async () => {
    if (!newComment) return;

    try {
      // ใช้คำสั่ง POST เพื่อเพิ่มคอมเม้นท์ใหม่ใน backend
      const response = await axios.post(`http://localhost:5000/pawpaw_test/comments/${post._id}`, {
        userId: 'userIdFromStateOrContext', // ใช้ ID ของผู้ใช้ที่ล็อกอิน
        content: newComment,
      });

      if (response.data.success) {
        setComments([...comments, response.data.comment]); // อัปเดตคอมเม้นท์ใน state
        setNewComment(''); // ล้างช่องเขียนคอมเม้นท์
      }
    } catch (error) {
      console.error('Failed to add comment', error);
    }
  };

  return (
    <Card key={post._id} sx={{ maxWidth: 600, margin: '20px auto' }}>
      <CardContent>
        <Typography variant="subtitle1" color="textSecondary">
          {post.username} • {new Date(post.createdAt).toLocaleDateString()}
        </Typography>
        <Typography variant="body1" sx={{ marginTop: '1rem' }}>
          {post.content}
        </Typography>

        {post.images && post.images.length > 0 && (
          <Grid container spacing={1} sx={{ marginTop: '1rem' }}>
            {post.images.map((image, index) => (
              <Grid item xs={6} key={index}>
                <img src={image.url} alt="" style={{ width: '100%', borderRadius: '4px' }} />
              </Grid>
            ))}
          </Grid>
        )}
      </CardContent>

      <CardActions>
        <IconButton aria-label="like" onClick={() => onLike(post._id)}>
          <FavoriteBorderIcon /> <Typography variant="body2" sx={{ marginLeft: 0.5 }}>{post.likes}</Typography>
        </IconButton>
        <IconButton aria-label="comment">
          <ReviewsIcon />
        </IconButton>
        <IconButton aria-label="share">
          <IosShareIcon />
        </IconButton>
      </CardActions>

      {/* Section สำหรับการเพิ่มคอมเม้นท์ */}
      <CardContent>
        {comments.length > 0 && (
          <>
            <Typography variant="h6">Comments</Typography>
            {comments.map((comment, index) => (
              <Typography key={index} variant="body2" sx={{ marginBottom: '1rem' }}>
                {comment.userId.username}: {comment.content}
              </Typography>
            ))}
          </>
        )}
        
        <TextField
          label="Add a comment"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          fullWidth
          sx={{ marginBottom: '1rem' }}
        />
        <Button onClick={handleAddComment} variant="contained">
          Comment
        </Button>
      </CardContent>
    </Card>
  );
};

export default Post;
