import React, { useState, useEffect } from 'react';
import { Box, Card, CardContent, CardActions, IconButton, Typography } from '@mui/material';
import Grid from '@mui/material/Grid';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import axios from 'axios';

const Post = () => {
  const [posts, setPosts] = useState([]);

  // ฟังก์ชันสำหรับดึงโพสต์ทั้งหมดจาก backend
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/pawpaw_test/posts/all');
        if (response.data.success) {
          setPosts(response.data.posts);
        }
      } catch (error) {
        console.error('Failed to fetch posts:', error);
      }
    };

    fetchPosts(); // เรียกใช้ fetchPosts เมื่อตัวคอมโพเนนต์ถูก mount
  }, []);

  return (
    <Box sx={{ width: '100%', maxWidth: '800px' }}>
      {posts.map((post) => (
        <Card key={post._id} sx={{ maxWidth: 600, margin: '20px auto' }}>
          <CardContent>
            <Typography variant="subtitle1" color="textSecondary">
              {post.username} • {new Date(post.createdAt).toLocaleDateString()}
            </Typography>
            <Typography variant="body1" sx={{ marginTop: '1rem' }}>
              {post.content}
            </Typography>

            {post.images && post.images.length > 0 && (
              <Grid container spacing={1} sx={{ marginTop: '1rem' }}>
                {post.images.slice(0, 4).map((image, index) => (
                  <Grid item xs={6} key={index}>
                    <img src={image.url} alt={`Post image ${index}`} style={{ width: '100%', borderRadius: '4px' }} />
                  </Grid>
                ))}
              </Grid>
            )}
          </CardContent>
          <CardActions>
            <IconButton aria-label="like">
              <FavoriteBorderIcon /> <Typography variant="body2" sx={{ marginLeft: 0.5 }}>{post.likes}</Typography>
            </IconButton>
            <IconButton aria-label="comment">
              <ReviewsIcon />
            </IconButton>
            <IconButton aria-label="share">
              <IosShareIcon />
            </IconButton>
          </CardActions>
        </Card>
      ))}
    </Box>
  );
};

export default Post;
