import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Post from '../components/Post'; // นำ component Post มาใช้เพื่อแสดงโพสต์ในหน้าโปรไฟล์

const Profile = () => {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');
  
    if (!token || !userId) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }
  
    // ใช้ข้อมูลจาก localStorage เพื่อตั้งค่าโปรไฟล์ของผู้ใช้โดยตรง
    setUser({
      _id: userId,
      // คุณสามารถเก็บข้อมูลอื่น ๆ แล้วใส่ตรงนี้ได้เช่นกัน เช่น username หรือ email
      username: localStorage.getItem('username'),
    });
    
  
    // ดึงโพสต์ของผู้ใช้
    axios.get(`http://localhost:5000/pawpaw_test/posts/user/${userId}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(response => {
      setPosts(response.data.posts); // ดึง posts จาก response.data.posts
    })
    .catch(error => {
      console.error('Error fetching user posts:', error);
    });
  }, [navigate]);
  

  if (!user) {
    return null; // ไม่แสดงอะไรถ้ายังไม่มีข้อมูลผู้ใช้หรือยังไม่ได้ล็อกอิน
  }

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        <div style={styles.profileHeader}>
          <img src={user.image || '/default-profile.png'} alt="Profile" style={styles.profileImage} />
          <h2>{user.username}</h2>
          <p>{user.profession || 'No profession provided'}</p>
        </div>
        <div style={styles.aboutSection}>
          <h3>About</h3>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone || 'No phone number provided'}</p>
          <p><strong>Location:</strong> {user.location || 'No location provided'}</p>
        </div>
      </div>

      <div style={styles.posts}>
        <h3>Posts</h3>
        {posts && posts.length > 0 ? (
          posts.map(post => (
            <Post key={post._id} post={post} /> // ใช้ Component Post ในการแสดงโพสต์
          ))
        ) : (
          <p>No posts available. Try posting something about your pet!</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileHeader: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  profileImage: {
    borderRadius: '50%',
    width: '120px',
    height: '120px',
    objectFit: 'cover',
  },
  aboutSection: {
    marginTop: '20px',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
};

export default Profile;
