// Comment.js
import React, { useState } from 'react';
import { TextField, Button, Typography, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';

const Comment = ({ postId, comments, onAddComment }) => {
  const [replyInputs, setReplyInputs] = useState({}); // Store input for each reply
  const [showReplyFields, setShowReplyFields] = useState({}); // Show/hide each reply input

  const toggleReplyField = (commentId) => {
    setShowReplyFields((prevState) => ({
      ...prevState,
      [commentId]: !prevState[commentId],
    }));
  };
  
  const handleReplyInputChange = (commentId, value) => {
    setReplyInputs((prevState) => ({
      ...prevState,
      [commentId]: value,
    }));
  };
  
  // ใน return JSX
  {showReplyFields[comment._id] && (
    <div style={{ display: 'flex', alignItems: 'center', marginLeft: '1rem', marginTop: '1rem' }}>
      <TextField
        label="Write a reply..."
        variant="outlined"
        value={replyInputs[comment._id] || ''}
        onChange={(e) => handleReplyInputChange(comment._id, e.target.value)}
        size="small"
        sx={{ flexGrow: 1, marginRight: '0.5rem' }}
      />
      <Button
        variant="contained"
        color="primary"
        onClick={() => handleReplySubmit(comment._id)}
      >
        Reply
      </Button>
    </div>
  )}
  
  const CommentCard = ({ comment }) => (
    <div style={{ marginTop: '1rem', padding: '10px', border: '1px solid #e0e0e0', borderRadius: '5px' }}>
      {/* Username, Date, and Time */}
      <Typography variant="subtitle2" color="textSecondary">
        {comment.userId?.username ? comment.userId.username : "Unknown"} • {new Date(comment.createdAt).toLocaleDateString()} • {new Date(comment.createdAt).toLocaleTimeString()}
      </Typography>
  
      {/* Content */}
      <Typography variant="body1" sx={{ marginBottom: '0.5rem' }}>
        {comment.content}
      </Typography>
  
      {/* Actions */}
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <LikeButton
          likedByUser={comment.likedByUser}
          onLike={() => {
            console.log(`Liked comment with ID: ${comment._id}`);
          }}
          likeCount={comment.likes ? comment.likes.length : 0}
        />
        <IconButton aria-label="reply" onClick={() => toggleReplyField(comment._id)} style={{ marginLeft: '0.5rem' }}>
          <ReviewsIcon />
        </IconButton>
        <IconButton aria-label="share" style={{ marginLeft: '0.5rem' }}>
          <IosShareIcon />
        </IconButton>
      </div>
  
      {/* Reply input field */}
      {showReplyFields[comment._id] && (
        <div style={{ display: 'flex', alignItems: 'center', marginLeft: '1rem', marginTop: '1rem' }}>
          <TextField
            label="Write a reply..."
            variant="outlined"
            value={replyInputs[comment._id] || ''}
            onChange={(e) => setReplyInputs((prevState) => ({
              ...prevState,
              [comment._id]: e.target.value,
            }))}
            size="small"
            sx={{ flexGrow: 1, marginRight: '0.5rem' }}
          />
          <Button variant="contained" color="primary" onClick={() => handleReplySubmit(comment._id)}>
            Reply
          </Button>
        </div>
      )}
    </div>
  );

  return (
    <div>
    {comments.map((comment) => (
      <React.Fragment key={comment._id}>
        <CommentCard comment={comment} />
        {/* Render replies */}
        {comment.replies && comment.replies.length > 0 && (
          <div style={{ marginLeft: '2rem', marginTop: '1rem' }}>
            {comment.replies.map((reply) => (
              <CommentCard key={reply._id} comment={reply} />
            ))}
          </div>
        )}
      </React.Fragment>
    ))}
  </div>
);
}

export default Comment;
