import React, { useState } from 'react';
import { Box, TextField, Button, Card, CardContent, Typography } from '@mui/material';
import ReviewsTwoToneIcon from '@mui/icons-material/ReviewsTwoTone';
import FavoriteTwoToneIcon from '@mui/icons-material/FavoriteTwoTone';
import FavoriteIcon from '@mui/icons-material/Favorite';
import IosShareIcon from '@mui/icons-material/IosShare';

const Forum = () => {
  const [posts, setPosts] = useState([]); // State to hold posts
  const [postContent, setPostContent] = useState(''); // State for new post content

  const handlePostSubmit = (e) => {
    e.preventDefault();
    if (postContent) {
      const newPost = {
        id: posts.length + 1,
        content: postContent,
        username: 'User', // Replace with actual username
      };
      setPosts([...posts, newPost]); // Add new post to the list
      setPostContent(''); // Clear input field
    }
  };

  return (
    <div>
      <h1>Forum</h1>
      <form onSubmit={handlePostSubmit}>
        <Box mb={2}>
          <TextField
            label="What's on your mind?"
            variant="outlined"
            value={postContent}
            onChange={(e) => setPostContent(e.target.value)}
            multiline
            rows={4}
            fullWidth
            required
          />
        </Box>
        <Button type="submit" variant="contained" color="primary">
          Post
        </Button>
      </form>

      <div style={{ marginTop: '20px' }}>
        <h2>Posts</h2>
        {posts.map((post) => (
          <Card key={post.id} style={{ margin: '10px 0', borderRadius: '15px', padding: '10px' }}>
            <CardContent>
              <Typography variant="h6" component="div">
                {post.username}
              </Typography>
              <Typography variant="body2" style={{ maxHeight: '100px', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                {post.content.length > 100 ? `${post.content.substring(0, 100)}...` : post.content}
                {post.content.length > 100 && (
                  <span style={{ cursor: 'pointer', color: 'blue' }} onClick={() => alert(post.content)}>
                    Read More
                  </span>
                )}
              </Typography>
              <div style={{ marginTop: '10px', display: 'flex', justifyContent: 'space-between' }}>
                <Button size="small" startIcon={<ReviewsTwoToneIcon />}>Comment</Button>
                <Button size="small" startIcon={post.liked ? <FavoriteIcon /> : <FavoriteTwoToneIcon />} onClick={() => {/* Handle like */}}>
                  {post.liked ? 'Liked' : 'Like'}
                </Button>
                <Button size="small" startIcon={<IosShareIcon />}>Share</Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Forum;
