// App.jsx
import React from 'react';
import Sidebar from './components/Sidebar';
import { Container } from '@mui/material'; // Optional if you want to use Container for layout
import Home from './components/Home';
import Profile from './components/Profile';
import Blog from './components/Blog'; // Ensure this matches your intended route
import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

const App = () => {
  return (
    <Router>
      <Sidebar />
      <Container style={{ marginLeft: '240px' }}> {/* Adjust based on Sidebar width */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/forum" element={<Blog />} /> {/* Ensure this matches your sidebar link */}
        </Routes>
      </Container>
    </Router>
  );
};

export default App;
