import React from 'react';
import { Box, Typography, Avatar, Card, CardContent } from '@mui/material';

const Post = ({ post }) => {
  return (
    <Card sx={{ display: 'flex', mb: 2, maxWidth: '100%', minHeight: '150px' }}>
      <CardContent sx={{ display: 'flex', flexDirection: 'column', width: '100%' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <Avatar>{post.username[0]}</Avatar>
          <Typography variant="h6" sx={{ ml: 2 }}>
            {post.username}
          </Typography>
          <Typography variant="body2" sx={{ ml: 'auto', color: 'gray' }}>
            {post.time}
          </Typography>
        </Box>
        <Typography variant="body1" sx={{ flexGrow: 1, whiteSpace: 'normal', overflow: 'hidden' }}>
          {post.content}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default Post;
