import React, { useState } from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, IconButton } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import AssignmentIndIcon from '@mui/icons-material/AssignmentInd';
import PetsIcon from '@mui/icons-material/Pets';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import MenuIcon from '@mui/icons-material/Menu';
import { Link } from 'react-router-dom';

const Sidebar = () => {
  const [open, setOpen] = useState(true); // true for expanded, false for collapsed

  const toggleSidebar = () => {
    setOpen(!open);
  };

  return (
    <Drawer
      variant="permanent"
      open={open}
      sx={{
        '& .MuiDrawer-paper': {
          width: open ? 240 : 70, // Width when expanded and collapsed
          transition: 'width 0.3s',
          overflowX: 'hidden',
          whiteSpace: 'nowrap',
        },
      }}
    >
      {/* Toggle Sidebar Button */}
      <IconButton onClick={toggleSidebar} sx={{ margin: '8px' }}>
        <MenuIcon />
      </IconButton>

      {/* List of Links */}
      <List>
        <ListItem button component={Link} to="/">
          <ListItemIcon><HomeIcon /></ListItemIcon>
          {open && <ListItemText primary="Home" />}
        </ListItem>
        <ListItem button component={Link} to="/profile">
          <ListItemIcon><AssignmentIndIcon /></ListItemIcon>
          {open && <ListItemText primary="Profile" />}
        </ListItem>
        <ListItem button component={Link} to="/forum">
          <ListItemIcon><PetsIcon /></ListItemIcon>
          {open && <ListItemText primary="Forum" />}
        </ListItem>
        <ListItem button component={Link} to="/blog">
          <ListItemIcon><LibraryBooksIcon /></ListItemIcon>
          {open && <ListItemText primary="Blog" />}
        </ListItem>
      </List>
    </Drawer>
  );
};

export default Sidebar;
