import React, { useState } from 'react';
import { Card, CardContent, CardActions, Button, TextField, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import ImageUpload from './ImageUpload';
import axios from 'axios';

const CreatePost = ({ onAddPost }) => {
  const [content, setContent] = useState('');
  const [images, setImages] = useState([]);
  const navigate = useNavigate();

  const handleImageChange = (selectedFiles) => {
    setImages(selectedFiles); // เก็บรูปภาพที่ผู้ใช้อัปโหลด (แค่ 1 รูป)
  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to create a post');
      navigate('/login');
      return;
    }

    try {
      // เตรียมข้อมูลรูปแบบ FormData สำหรับการอัปโหลดรูปภาพ
      const formData = new FormData();
      formData.append('content', content);
      images.forEach((image) => {
        formData.append('images', image);
      });

      // สร้างคำขอ POST ไปยัง Backend
      const response = await axios.post('http://localhost:5000/pawpaw_test/create', 
        formData, 
        { headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      if (response.data.success) {
        const newPost = {
          userId: response.data.post.userId,
          content: response.data.post.content,
          images: response.data.post.images,
          likes: response.data.post.likes,
          createdAt: response.data.post.createdAt,
        };
        onAddPost(newPost);
        setContent('');
        setImages([]);
        console.log('Creating post with content:', content);

      }
    } catch (error) {
      console.error('Failed to create post', error);
    }
  };

  return (
    <Card sx={{ maxWidth: 600, margin: '20px auto' }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          Create a Post
        </Typography>
        <TextField
          label="What's on your mind?"
          multiline
          rows={4}
          value={content}
          onChange={(e) => setContent(e.target.value)}
          fullWidth
          sx={{ marginBottom: '1rem' }}
        />
        {/* ใช้ ImageUpload component */}
        <ImageUpload onImagesChange={handleImageChange} />
        {images.length > 0 && (
          <Typography variant="body2" color="textSecondary">
            {images.length} image(s) selected
          </Typography>
        )}
      </CardContent>
      <CardActions>
        <Button variant="contained" color="primary" onClick={handleSubmit}>
          Post
        </Button>
      </CardActions>
    </Card>
  );
};

export default CreatePost;
