//
import React, { useState } from 'react';
import { TextField, Button, Typography, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';

const Comment = ({ postId, comment, onAddComment }) => {
    const [commentInputs, setCommentInputs] = useState({});
    const [showReplyFields, setShowReplyFields] = useState({});
    const [replyInputs, setReplyInputs] = useState({});
  
  // Handle comment field visibility toggle
  const toggleCommentField = (commentId) => {
    setShowCommentFields((prevState) => ({
      ...prevState,
      [commentId]: !prevState[commentId],
    }));
  };

  // Handle comment input value change
  const handleCommentInputChange = (commentId, value) => {
    setCommentInputs((prevState) => ({
      ...prevState,
      [commentId]: value,
    }));
  };

  const handleCommentSubmit = async () => {
    if (comment.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to comment.');
          return;
        }

        // Send comment to backend
        const response = await axios.post(`http://localhost:5000/pawpaw_test/posts/${postId}/comment`, {
          content: comment
        }, {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (response.data.success) {
          onAddComment(response.data.comment); // Use the onAddComment prop to add the new comment
          setCommentInputs((prevState) => ({
            ...prevState,
            [commentId]: '', // Clear the comment input field
          }));
          setShowCommentFields((prevState) => ({
            ...prevState,
            [commentId]: false, // Hide the comment field after submitting
          }));
        }
      } catch (error) {
        console.error('Failed to create comment:', error);
      }
    }
  };

  return (
    <div>
    {comments.map((comment) => (
      <div key={comment._id} style={{ marginTop: '1rem', padding: '10px', border: '1px solid #e0e0e0', borderRadius: '5px' }}>
        <Typography variant="body2" sx={{ marginBottom: '0.5rem' }}>
          {comment.username ? comment.username : "Unknown"}: {comment.content}
        </Typography>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          {/* Replace with LikeButton component */}
          <LikeButton 
            likedByUser={comment.likedByUser} 
            onLike={() => {
              // Add your like logic here or pass props accordingly
              console.log(`Liked comment with ID: ${comment._id}`);
            }} 
            likeCount={comment.likes ? comment.likes.length : 0}
          />

          {/* Comment Icon to toggle comment input field */}
          <IconButton 
            aria-label="comment" 
            onClick={() => toggleCommentField(comment._id)}
          >
            <ReviewsIcon />
          </IconButton>

          {/* Share Icon */}
          <IconButton aria-label="share">
            <IosShareIcon />
          </IconButton>

          {/* Show comment input field when icon is clicked */}
          {showCommentFields[comment._id] && (
            <div style={{ display: 'flex', alignItems: 'center', marginLeft: '1rem', flexGrow: 1 }}>
              <TextField
                label="Write a comment..."
                variant="outlined"
                value={commentInputs[comment._id] || ''}
                onChange={(e) => handleCommentInputChange(comment._id, e.target.value)}
                size="small"
                sx={{ flexGrow: 1, marginRight: '0.5rem' }}
              />
              <Button
                variant="contained"
                color="primary"
                onClick={() => handleCommentSubmit(comment._id)}
              >
                Comment
              </Button>
            </div>
          )}
        </div>
      </div>
    ))}
  </div>
  );
};

export default Comment;
