import React, { useState } from 'react';
import MiniDrawer from './components/Sidebar'; // ใช้ Sidebar ที่ปรับปรุงแล้ว
import Login from './components/Login'; // อย่าลืมนำเข้า Login

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = () => {
        setIsLoggedIn(true);
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
    };

    return (
        <>
            <MiniDrawer isLoggedIn={isLoggedIn} onLogout={handleLogout} />
            {/* ส่ง handleLogin ไปที่ Login */}
            <Login onLogin={handleLogin} />
        </>
    );
};

export default App;
