import React, { useState } from 'react';
import { Card, CardContent, CardActions, Button, TextField, Typography, Box } from '@mui/material';
import axios from 'axios';

const CreatePost = ({ onPostCreated }) => {
  const [content, setContent] = useState('');
  const [images, setImages] = useState([]);
  
  const handleImageChange = (e) => {
    setImages([...e.target.files]); // เก็บรูปภาพที่ผู้ใช้อัปโหลด
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('content', content);
    images.forEach((image, index) => {
      formData.append(`images`, image); // เพิ่มรูปภาพทั้งหมดเข้าไปใน formData
    });

    try {
      const response = await axios.post('http://localhost:5000/pawpaw_test/posts/create', 
        formData, 
        {
          headers: { 
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }
      );
      if (response.data.success) {
        onPostCreated();
        setContent('');
        setImages([]);
      }
    } catch (error) {
      console.error('Failed to create post', error);
    }
  };

  return (
    <Card sx={{ maxWidth: 600, margin: '20px auto' }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          Create a Post
        </Typography>
        <TextField
          label="What's on your mind?"
          multiline
          rows={4}
          value={content}
          onChange={(e) => setContent(e.target.value)}
          fullWidth
          sx={{ marginBottom: '1rem' }}
        />
        {/* Image upload area */}
        <Box sx={{ marginBottom: '1rem' }}>
          <Button
            variant="outlined"
            component="label"
          >
            Upload Images
            <input
              type="file"
              hidden
              multiple
              accept="image/*"
              onChange={handleImageChange}
            />
          </Button>
          {images.length > 0 && (
            <Typography variant="body2" color="textSecondary">
              {images.length} image(s) selected
            </Typography>
          )}
        </Box>
      </CardContent>
      <CardActions>
        <Button variant="contained" color="primary" onClick={handleSubmit}>
          Post
        </Button>
      </CardActions>
    </Card>
  );
};

export default CreatePost;
