import React, { useState } from 'react';
import { Box, TextField, Button, Avatar } from '@mui/material';

const PostForm = ({ onSubmit, userProfile }) => {
    const [content, setContent] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!content) return;
        onSubmit(content);
        setContent(''); // Clear input after submission
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', alignItems: 'center', marginBottom: '2rem' }}>
            <Avatar src={userProfile.picture} alt={userProfile.username} sx={{ marginRight: '1rem' }} />
            <TextField
                fullWidth
                variant="outlined"
                placeholder="Write something..."
                value={content}
                onChange={(e) => setContent(e.target.value)}
                multiline
                rows={3}
            />
            <Button type="submit" variant="contained" sx={{ marginLeft: '1rem' }}>
                Post
            </Button>
        </Box>
    );
};

export default PostForm;
