// LikeButton.js
import React, { useState, useEffect } from 'react';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import { IconButton, Typography } from '@mui/material';

const LikeButton = ({ likedByUser, onLike, likeCount }) => {
  const [liked, setLiked] = useState(likedByUser);
  const [count, setCount] = useState(likeCount);

  // อัพเดตสถานะเมื่อ prop เปลี่ยน
  useEffect(() => {
    setLiked(likedByUser);
    setCount(likeCount);
  }, [likedByUser, likeCount]);

  // การกดไลค์
  const handleLikeClick = () => {
    // Update the liked state based on its previous value
    setLiked((prevLiked) => {
      const newLiked = !prevLiked;
      
      // Update the like count accordingly
      setCount((prevCount) => newLiked ? prevCount + 1 : prevCount - 1);
  
      // Call onLike to handle the backend
      onLike();
  
      // Return the updated liked state
      return newLiked;
    });
  };
  

  return (
    <div style={{ display: 'flex', alignItems: 'center' }}>
      <IconButton onClick={handleLikeClick}>
        {liked ? <FavoriteIcon color="error" /> : <FavoriteBorderIcon />}
      </IconButton>
      <Typography variant="body2">{count} Likes</Typography>
    </div>
  );
};

export default LikeButton;
