import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBCardText, MDBCardImage, MDBTypography, MDBIcon } from 'mdb-react-ui-kit';
import Post from '../components/Post';  // คอมโพเนนต์สำหรับโพสต์

export default function Profile() {
  const [user, setUser] = useState({});
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');

    if (!token || !userId) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }

    // ดึงข้อมูลโปรไฟล์จาก API
    axios
      .get('http://localhost:5000/pawpaw_test/profile', {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });

    // ดึงโพสต์ของผู้ใช้จาก API
    axios
      .get(`http://localhost:5000/pawpaw_test/posts/user/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setPosts(response.data.posts);
      })
      .catch((error) => {
        console.error('Error fetching posts:', error);
      });
  }, [navigate]);

  return (
    <section className="vh-100" style={{ backgroundColor: '#f4f5f7' }}>
      <MDBContainer className="py-5 h-100">
        <MDBRow className="justify-content-center align-items-center h-100" style={{ display: 'flex' }}>
          {/* ข้อมูลโปรไฟล์ */}
          <MDBCol lg="4" className="mb-4 mb-lg-0">
            <MDBCard className="mb-3" style={{ borderRadius: '.5rem' }}>
              <MDBRow className="g-0">
                <MDBCol md="12" className="gradient-custom text-center text-white"
                  style={{ borderTopLeftRadius: '.5rem', borderBottomLeftRadius: '.5rem' }}>
                  <MDBCardImage src={user.profileImage || "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp"}
                    alt="Avatar" className="my-5" style={{ width: '100px', height: '100px', borderRadius: '50%' }} fluid />
                  <MDBTypography tag="h5">{user.username}</MDBTypography>
                  <MDBCardText>{user.jobTitle || 'Web Designer'}</MDBCardText>
                  <MDBIcon far icon="edit mb-5" />
                </MDBCol>
              </MDBRow>
              <MDBCardBody>
                <MDBTypography tag="h6">Information</MDBTypography>
                <hr className="mt-0 mb-4" />
                <MDBRow className="pt-1">
                  <MDBCol size="6" className="mb-3">
                    <MDBTypography tag="h6">Email</MDBTypography>
                    <MDBCardText className="text-muted">{user.email || 'N/A'}</MDBCardText>
                  </MDBCol>
                  <MDBCol size="6" className="mb-3">
                    <MDBTypography tag="h6">Phone</MDBTypography>
                    <MDBCardText className="text-muted">{user.profile?.phone || 'N/A'}</MDBCardText>
                  </MDBCol>
                </MDBRow>
                <div className="d-flex justify-content-start">
                  <a href="#!"><MDBIcon fab icon="facebook me-3" size="lg" /></a>
                  <a href="#!"><MDBIcon fab icon="twitter me-3" size="lg" /></a>
                  <a href="#!"><MDBIcon fab icon="instagram me-3" size="lg" /></a>
                </div>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>

          {/* โพสต์ของผู้ใช้ */}
          <MDBCol lg="8" className="mb-4 mb-lg-0">
            <h3>Posts</h3>
            <div>
              {posts.length > 0 ? (
                posts.map((post) => <Post key={post._id} post={post} />)
              ) : (
                <p>No posts available. Please share your thoughts!</p>
              )}
            </div>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}
