import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EditProfile = ({ user, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    username: user.username,
    email: user.email,
    phone: user.phone,
    birthday: user.birthday,
    bio: user.bio,
    petTypes: user.petTypes,
    showEmail: user.showEmail,
    profileImage: user.profileImage,
  });

  useEffect(() => {
    // หากข้อมูลผู้ใช้มีการเปลี่ยนแปลง จะรีเฟรชข้อมูลในฟอร์ม
    setFormData({
      username: user.username,
      email: user.email,
      phone: user.phone,
      birthday: user.birthday,
      bio: user.bio,
      petTypes: user.petTypes,
      showEmail: user.showEmail,
      profileImage: user.profileImage,
    });
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      // ส่งข้อมูลไปที่ backend เพื่ออัปเดตโปรไฟล์
      const response = await axios.put(
        'http://localhost:5000/pawpaw_test/profile',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      onSave(response.data); // ส่งข้อมูลที่อัปเดตแล้วไปยัง Profile.js
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Failed to update profile');
    }
  };

  return (
    <div style={styles.editProfileContainer}>
      <h3>Edit Profile</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Phone:
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
          />
        </label>
        <label>
          Birthday:
          <input
            type="date"
            name="birthday"
            value={formData.birthday}
            onChange={handleChange}
          />
        </label>
        <label>
          Bio:
          <textarea
            name="bio"
            value={formData.bio}
            onChange={handleChange}
          />
        </label>
        <label>
          Pet Types:
          <input
            type="text"
            name="petTypes"
            value={formData.petTypes}
            onChange={handleChange}
            placeholder="Enter pet types separated by commas"
          />
        </label>
        <label>
          Show Email:
          <input
            type="checkbox"
            name="showEmail"
            checked={formData.showEmail}
            onChange={(e) => setFormData({ ...formData, showEmail: e.target.checked })}
          />
        </label>
        <label>
          Profile Image:
          <input
            type="text"
            name="profileImage"
            value={formData.profileImage}
            onChange={handleChange}
            placeholder="Enter image URL"
          />
        </label>
        <button type="submit">Save Changes</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </form>
    </div>
  );
};

const styles = {
  editProfileContainer: {
    padding: '20px',
    backgroundColor: '#f4f4f4',
    borderRadius: '8px',
  },
};

export default EditProfile;
