import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { Box } from '@mui/material';

const Forum = () => {
  const [posts, setPosts] = useState([]); // State to hold posts
  const [postContent, setPostContent] = useState(''); // State for new post content

  const handlePostSubmit = (e) => {
    e.preventDefault();
    if (postContent) {
      const newPost = {
        id: posts.length + 1,
        content: postContent,
        username: 'User', // This should be the logged-in user's username
        // Add other fields as necessary
      };
      setPosts([...posts, newPost]); // Add new post to the list
      setPostContent(''); // Clear input field
    }
  };

  return (
    <div>
      <h1>Forum</h1>
      <form onSubmit={handlePostSubmit}>
        <Box mb={2}>
          <TextField
            label="What's on your mind?"
            variant="outlined"
            value={postContent}
            onChange={(e) => setPostContent(e.target.value)}
            multiline
            rows={4} // Adjust as necessary
            fullWidth
            required
          />
        </Box>
        <Button type="submit" variant="contained" color="primary">
          Post
        </Button>
      </form>
      <div style={{ marginTop: '20px' }}>
        <h2>Posts</h2>
        {posts.map((post) => (
          <div key={post.id} style={{ border: '1px solid #ccc', margin: '10px 0', padding: '10px' }}>
            <p><strong>{post.username}:</strong> {post.content}</p>
            {/* Add other post details here (like date, image, etc.) */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Forum;
