import React, { useState } from 'react';
import axios from 'axios';

const Reply = ({ comment, postId, fetchComments }) => {
  const [reply, setReply] = useState('');
  const [showReplyInput, setShowReplyInput] = useState(false);

  const handleReply = async () => {
    if (reply.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
        { content: reply, parentCommentId: comment._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setReply('');
      setShowReplyInput(false);
      fetchComments(); // Refresh comments
    } catch (error) {
      console.error('Error replying to comment:', error);
    }
  };

  return (
    <div style={{ marginLeft: comment.parentCommentId ? '20px' : '0px' }}>
      <p>
        <strong>{comment.userId?.username}</strong>: {comment.content}
      </p>
      <button onClick={() => setShowReplyInput(!showReplyInput)}>Reply</button>
      {showReplyInput && (
        <div>
          <textarea
            value={reply}
            onChange={(e) => setReply(e.target.value)}
            placeholder="Write a reply..."
            style={{ width: '100%', minHeight: '50px' }}
          />
          <button onClick={handleReply}>Post Reply</button>
        </div>
      )}
      {/* Display nested replies */}
      {comment.replies && comment.replies.length > 0 && (
        <div style={{ marginLeft: '20px' }}>
          {comment.replies.map((replyComment) => (
            <Reply
              key={replyComment._id}
              comment={replyComment}
              postId={postId}
              fetchComments={fetchComments}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Reply;
