import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Fab } from '@mui/material';
import Post from './Post'; // Import Post component
import CreateIcon from '@mui/icons-material/Create';

const Forum = () => {
  const [posts, setPosts] = useState([]); // State to hold posts
  const [postContent, setPostContent] = useState(''); // State for new post content
  const [isInputVisible, setIsInputVisible] = useState(true); // State to control input visibility

  const handlePostSubmit = (e) => {
    e.preventDefault();
    if (postContent) {
      const newPost = {
        id: posts.length + 1,
        content: postContent,
        username: 'User', // Replace with actual username
      };
      setPosts([...posts, newPost]); // Add new post to the list
      setPostContent(''); // Clear input field
    }
  };

  // Handle scroll to toggle visibility of the input area
  const handleScroll = () => {
    if (window.scrollY > 100) { // Adjust the scroll position threshold as needed
      setIsInputVisible(false);
    } else {
      setIsInputVisible(true);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div>
      <h1>Forum</h1>
      {isInputVisible && (
        <form onSubmit={handlePostSubmit}>
          <Box mb={2}>
            <TextField
              label="What's on your mind?"
              variant="outlined"
              value={postContent}
              onChange={(e) => setPostContent(e.target.value)}
              multiline
              rows={4}
              fullWidth
              required
            />
          </Box>
          <Button type="submit" variant="contained" color="primary">
            Post
          </Button>
        </form>
      )}
      <div style={{ marginTop: '20px' }}>
        <h2>Posts</h2>
        {posts.map((post) => (
          <Post key={post.id} post={post} />
        ))}
      </div>
      {!isInputVisible && (
        <Fab 
          color="primary" 
          aria-label="create" 
          style={{ position: 'fixed', bottom: '20px', right: '20px' }}
          onClick={() => setIsInputVisible(true)} // Show input area again
        >
          <CreateIcon />
        </Fab>
      )}
    </div>
  );
};

export default Forum;
