import React, { useState, useEffect } from 'react';
import { Box, Typography, IconButton, TextField, Button, Card } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import DeleteButton from './DeleteButton';
import { shareContent } from './shareUtil';
import Reply from './Reply';
import axios from 'axios';

const Comment = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [showCommentBox, setShowCommentBox] = useState(false); // state สำหรับแสดง/ซ่อน textarea
  const [currentUserId, setCurrentUserId] = useState(null);
  
  const userId = localStorage.getItem('userId');
    if (userId) {
      setCurrentUserId(userId);
      console.log("Loaded User ID in Comment:", userId);
    } else {
      console.error("User ID is not set in localStorage");
  }


  useEffect(() => {
    fetchComments();
  }, []);

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleAddComment = async () => {
    if (newComment.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
        { content: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setComments([response.data.comment, ...comments]);
      setNewComment('');
      setShowCommentBox(false); // ซ่อน textarea หลังจากโพสต์สำเร็จ
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const handleLikeComment = async (commentId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to like the comment.');
        return false;
      }
  
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/comments/${commentId}/like`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
  
      if (response.data.success) {
        setComments((prevComments) =>
          prevComments.map((comment) =>
            comment._id === commentId
              ? { ...comment, likes: response.data.likes } // อัปเดต likes ใน comment ที่ตรงกับ ID
              : comment
          )
        );
        return true;
      }
    } catch (error) {
      console.error('Failed to like/unlike the comment', error);
    }
    return false;
  };

  const handleSharePost = () => {
    shareContent({
      title: commentId.content || 'Check out this post!',
      text: commentId.content,
      url: `${window.location.origin}/posts/${commentId}`,
    });
  };

  const handleDeleteComment = async (commentId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to delete the comment.');
        return;
      }
  
      await axios.delete(`http://localhost:5000/pawpaw_test/comments/${commentId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
  
      // ลบคอมเมนต์ออกจาก state
      setComments((prevComments) => prevComments.filter((comment) => comment._id !== commentId));
      alert('Comment deleted successfully.');
    } catch (error) {
      console.error('Failed to delete comment:', error);
    }
  };

  return (
    <Card
      sx={{
        maxWidth: 800,
        margin: '20px auto',
        borderRadius: '16px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        padding: '16px',
      }}
    >
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
          Comments
        </Typography>
        <IconButton
          aria-label="toggle-comment-box"
          onClick={() => setShowCommentBox(!showCommentBox)}
        >
          <ReviewsIcon />
        </IconButton>
      </Box>

      {showCommentBox && (
        <Box sx={{ marginTop: '16px' }}>
          <TextField
            placeholder="Write a comment..."
            variant="outlined"
            fullWidth
            multiline
            rows={3}
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            sx={{ marginBottom: '8px' }}
          />
          <Button variant="contained" color="primary" onClick={handleAddComment}>
            Post Comment
          </Button>
        </Box>
      )}

      {comments.map((comment) => (
        <Box
          key={comment._id}
          sx={{
            marginTop: '16px',
            padding: '8px',
            border: '1px solid #e0e0e0',
            borderRadius: '8px',
            position: 'relative',
          }}
        >
          {currentUserId && comment.userId && currentUserId === comment.userId.toString() && (
            <Box sx={{ position: 'absolute', top: '8px', right: '8px' }}>
              <DeleteButton onDelete={() => handleDeleteComment(comment._id)} />
            </Box>
          )}
          <Typography variant="subtitle2" color="textSecondary">
            {comment.userId?.username || 'Unknown User'} •{' '}
            {new Date(comment.createdAt).toLocaleDateString()} •{' '}
            {new Date(comment.createdAt).toLocaleTimeString()}
          </Typography>
          <Typography variant="body1" sx={{ margin: '8px 0' }}>
            {comment.content}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <LikeButton
              likedByUser={currentUserId ? comment.likes.includes(currentUserId) : false}
              onLike={() => handleLikeComment(comment._id)}
              likeCount={comment.likes.length}
            />
            <IconButton aria-label="share" onClick={() => handleShareComment(comment)}>
              <IosShareIcon />
            </IconButton>
          </Box>
        </Box>
      ))}
    </Card>
  );
};

export default Comment;
