import React, { useState } from 'react';
import { TextField, Button, Typography } from '@mui/material';
import axios from 'axios';

const Comment = ({ postId, comments, onAddComment }) => {
  const [comment, setComment] = useState('');

  const handleCommentSubmit = async () => {
    if (comment.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to comment.');
          return;
        }

        const response = await axios.post(`http://localhost:5000/pawpaw_test/${postId}/comment`, {
          content: comment
        }, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        if (response.data.success) {
          onAddComment(response.data.comment);
          setComment('');
        } else {
          console.error('Comment submission failed:', response.data.message);
        }
      } catch (error) {
        console.error('Failed to create comment:', error);
      }
    }
  };

  return (
    <div>
      {comments.map((comment) => (
        <Typography key={comment._id} variant="body2" sx={{ marginTop: '0.5rem' }}>
          {comment.username}: {comment.content}
        </Typography>
      ))}
      <TextField
        label="Write a comment..."
        variant="outlined"
        value={comment}
        onChange={(e) => setComment(e.target.value)}
        size="small"
        fullWidth
        sx={{ marginTop: '1rem' }}
      />
      <Button variant="contained" color="primary" onClick={handleCommentSubmit} sx={{ marginTop: '0.5rem' }}>
        Comment
      </Button>
    </div>
  );
};

export default Comment;
