import React, { useState, memo, useEffect } from 'react';
import { TextField, Button, Typography, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';

const Comment = ({ postId, initialComments }) => {
  const [comments, setComments] = useState(initialComments || []);
  const [replyInputs, setReplyInputs] = useState({});
  const [showReplyFields, setShowReplyFields] = useState({});

  // Fetch comments when component mounts
  useEffect(() => {
    if (!initialComments) {
      fetchComments();
    }
  }, []);

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postId}/comments`);
      if (response.data.success) {
        setComments(response.data.comments);
      }
    } catch (error) {
      console.error('Failed to fetch comments:', error);
    }
  };

  const handleReplySubmit = async (parentCommentId) => {
    const replyContent = replyInputs[parentCommentId];
    if (replyContent && replyContent.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to reply.');
          return;
        }

        const response = await axios.post(
          `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
          {
            content: replyContent,
            parentCommentId,
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (response.data.success) {
          const newComment = response.data.comment;

          // Update state to include new reply
          setComments((prevComments) =>
            prevComments.map((comment) =>
              comment._id === parentCommentId
                ? { ...comment, replies: [...(comment.replies || []), newComment] }
                : comment
            )
          );

          setReplyInputs((prevState) => ({
            ...prevState,
            [parentCommentId]: '',
          }));
        }
      } catch (error) {
        console.error('Failed to create reply:', error);
      }
    }
  };

  const toggleReplyField = (commentId) => {
    setShowReplyFields((prevState) => ({
      ...prevState,
      [commentId]: !prevState[commentId],
    }));
  };

  const CommentCard = memo(({ comment }) => (
    <div
      style={{
        marginTop: '1rem',
        padding: '10px',
        border: '1px solid #e0e0e0',
        borderRadius: '5px',
      }}
    >
      {/* Username, Date, and Time */}
      <Typography variant="subtitle2" color="textSecondary">
        {comment.userId?.username ? comment.userId.username : 'Unknown'} •{' '}
        {new Date(comment.createdAt).toLocaleDateString()} •{' '}
        {new Date(comment.createdAt).toLocaleTimeString()}
      </Typography>

      {/* Content */}
      <Typography variant="body1" sx={{ marginBottom: '0.5rem' }}>
        {comment.content}
      </Typography>

      {/* Actions */}
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <LikeButton
          likedByUser={comment.likedByUser}
          onLike={() => {
            console.log(`Liked comment with ID: ${comment._id}`);
          }}
          likeCount={comment.likes ? comment.likes.length : 0}
        />
        <IconButton
          aria-label="reply"
          onClick={() => toggleReplyField(comment._id)}
          style={{ marginLeft: '0.5rem' }}
        >
          <ReviewsIcon />
        </IconButton>
        <IconButton aria-label="share" style={{ marginLeft: '0.5rem' }}>
          <IosShareIcon />
        </IconButton>
      </div>

      {/* Reply input field */}
      {showReplyFields[comment._id] && (
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            marginLeft: '1rem',
            marginTop: '1rem',
          }}
        >
          <TextField
            label="Write a reply..."
            variant="outlined"
            value={replyInputs[comment._id] || ''}
            onChange={(e) =>
              setReplyInputs((prevState) => ({
                ...prevState,
                [comment._id]: e.target.value,
              }))
            }
            size="small"
            sx={{ flexGrow: 1, marginRight: '0.5rem' }}
            autoFocus
          />
          <Button
            variant="contained"
            color="primary"
            onClick={() => handleReplySubmit(comment._id)}
          >
            Reply
          </Button>
        </div>
      )}

      {/* Display replies */}
      {comment.replies && comment.replies.length > 0 && (
        <div style={{ marginLeft: '2rem', marginTop: '1rem' }}>
          {comment.replies.map((reply) => (
            <CommentCard key={reply._id} comment={reply} />
          ))}
        </div>
      )}
    </div>
  ));

  return (
    <div>
      {comments.length > 0 ? (
        comments.map((comment) => (
          <CommentCard key={comment._id} comment={comment} />
        ))
      ) : (
        <Typography variant="body1" sx={{ marginTop: '1rem' }}>
          No comments available.
        </Typography>
      )}
    </div>
  );
};

export default Comment;
