import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Post from '../components/Post';
import EditProfile from '../components/EditProfile';

const Profile = () => {
  const [user, setUser] = useState(null);  // เริ่มต้นด้วย null
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');

    if (!token || !userId) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }

    // ดึงข้อมูลโปรไฟล์
    axios
    .get('http://localhost:5000/pawpaw_test/profile', {
      headers: {
        Authorization: `Bearer ${token}`,  // ใช้ token ที่เก็บไว้ใน localStorage
      },
    })
    .then((response) => {
      console.log("User data from API:", response.data);  // ตรวจสอบข้อมูลที่ได้รับจาก API
      setUser(response.data);  // กำหนดข้อมูล user ที่ได้รับจาก API
    })
    .catch((error) => {
      console.error('Error fetching user data:', error);
      alert('Failed to load user profile. Please try again.');
    });

    // ดึงโพสต์ของผู้ใช้
    axios
      .get(`http://localhost:5000/pawpaw_test/posts/user/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setPosts(response.data.posts);  // ตั้งค่า posts
      })
      .catch((error) => {
        console.error('Error fetching user posts:', error);
      });
  }, [navigate]);

  // ฟังก์ชันสำหรับเปิด/ปิดโหมดแก้ไข
  const toggleEditMode = () => {
    setIsEditing(!isEditing);
  };

  // ฟังก์ชันสำหรับบันทึกการแก้ไขโปรไฟล์
  const handleSave = (updatedUser) => {
    setUser(updatedUser);
    setIsEditing(false);
  };

  if (!user) {
    return null;  // ถ้า user ยังไม่มีข้อมูลหรือกำลังโหลด ให้ไม่แสดงอะไร
  }

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        {!isEditing ? (
          <>
            <div style={styles.profileHeader}>
              <img
                src={user.profileImage || '/default-profile.png'}
                alt="Profile"
                style={styles.profileImage}
              />
              <h2>{user.username}</h2>
            </div>
            <div style={styles.aboutSection}>
              <h3>About</h3>
              {user.showEmail && <p><strong>Email:</strong> {user.email}</p>}
              <p><strong>Bio:</strong> {user.bio}</p>
              <p><strong>Birthday:</strong> {user.birthday ? new Date(user.birthday).toLocaleDateString() : 'Not provided'}</p>
              <p><strong>Pet Types:</strong> {user.petTypes ? user.petTypes.join(', ') : 'No pets added'}</p>
            </div>
            <button style={styles.editButton} onClick={toggleEditMode}>
              Edit Profile
            </button>
          </>
        ) : (
          <EditProfile user={user} onSave={handleSave} onCancel={toggleEditMode} />
        )}
      </div>

      <div style={styles.posts}>
        <h3>Posts</h3>
        {posts.length > 0 ? (
          posts.map((post) => (
            <Post key={post._id} post={post} />
          ))
        ) : (
          <p>No posts available. Try posting something about your pet!</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileHeader: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  profileImage: {
    borderRadius: '50%',
    width: '120px',
    height: '120px',
    objectFit: 'cover',
  },
  aboutSection: {
    marginTop: '20px',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
  editButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    borderRadius: '5px',
  },
};

export default Profile;
