import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, TextField, Button, Checkbox, FormControlLabel, FormGroup, Select, MenuItem } from '@mui/material';

const EditProfile = ({ user, onSave }) => {
  const [formData, setFormData] = useState({
    phone: user.phone || '',
    birthday: user.birthday || '',
    bio: user.bio || '',
    petTypes: user.petTypes || [],
    showEmail: user.showEmail || false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCheckboxChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.checked });
  };

  const handlePetTypesChange = (event) => {
    setFormData({ ...formData, petTypes: event.target.value });
  };

  const handleSave = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put('http://localhost:5000/pawpaw_test/user/profile', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Profile updated successfully');
      onSave(); // อัปเดตหน้าโปรไฟล์ใหม่
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Failed to update profile');
    }
  };

  return (
    <Box>
      <h2>Edit Profile</h2>
      <form>
        <TextField
          label="Phone"
          name="phone"
          value={formData.phone}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <TextField
          label="Birthday"
          name="birthday"
          type="date"
          value={formData.birthday}
          onChange={handleChange}
          fullWidth
          margin="normal"
          InputLabelProps={{ shrink: true }}
        />
        <TextField
          label="Bio"
          name="bio"
          value={formData.bio}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <FormGroup>
          <FormControlLabel
            control={
              <Checkbox
                checked={formData.showEmail}
                onChange={handleCheckboxChange}
                name="showEmail"
              />
            }
            label="Show Email on Profile"
          />
        </FormGroup>
        <FormControlLabel>
          <label>Pet Types:</label>
          <Select
            multiple
            value={formData.petTypes}
            onChange={handlePetTypesChange}
            fullWidth
          >
            {['Cat', 'Dog', 'Bird', 'Fish', 'Rabbit', 'Turtle', 'Other'].map((pet) => (
              <MenuItem key={pet} value={pet}>
                {pet}
              </MenuItem>
            ))}
          </Select>
        </FormControlLabel>
        <Button variant="contained" color="primary" onClick={handleSave}>
          Save Changes
        </Button>
      </form>
    </Box>
  );
};

export default EditProfile;
