import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    bio: '',
    petTypes: '',
    birthday: '',
    showEmail: false
  });

  // ดึงข้อมูลโปรไฟล์จาก API เมื่อ component โหลด
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return; // ถ้าไม่มี token จะไม่โหลดข้อมูล

    axios.get('/pawpaw_test/profile', {
      headers: { Authorization: `Bearer ${token}` } // ส่ง token เพื่อยืนยันตัวตน
    })
    .then(res => {
      setProfile(res.data);
      setFormData({
        bio: res.data.profile.bio || '',
        petTypes: res.data.profile.petTypes.join(', ') || '',
        birthday: res.data.profile.birthday ? new Date(res.data.profile.birthday).toISOString().split('T')[0] : '',
        showEmail: res.data.profile.showEmail || false
      });
    })
    .catch(err => console.log(err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.put('/pawpaw_test/profile', formData, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    })
    .then(res => {
      setProfile(res.data);
      setIsEditing(false);
    })
    .catch(err => console.log(err));
  };

  return (
    <div>
      {profile && !isEditing ? (
        <div>
          <h2>{profile.username}'s Profile</h2>
          <p><strong>Username:</strong> {profile.username}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          <p><strong>Bio:</strong> {profile.profile.bio || 'Not provided'}</p>
          <p><strong>Pet Types:</strong> {profile.profile.petTypes.length > 0 ? profile.profile.petTypes.join(', ') : 'Not provided'}</p>
          <p><strong>Birthday:</strong> {profile.profile.birthday ? new Date(profile.profile.birthday).toLocaleDateString() : 'Not provided'}</p>
          <p><strong>Email visibility:</strong> {profile.profile.showEmail ? profile.email : 'Hidden'}</p>

          {/* ปุ่ม Edit Profile */}
          <button onClick={() => setIsEditing(true)}>Edit Profile</button>
        </div>
      ) : (
        <div>
          <h2>Edit Profile</h2>
          <form onSubmit={handleSubmit}>
            {/* ฟิลด์เพื่อแก้ไขข้อมูล */}
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Your bio"
            />
            <input
              type="text"
              name="petTypes"
              value={formData.petTypes}
              onChange={handleChange}
              placeholder="Pet Types (comma-separated)"
            />
            <input
              type="date"
              name="birthday"
              value={formData.birthday}
              onChange={handleChange}
            />
            <label>
              Show Email:
              <input
                type="checkbox"
                name="showEmail"
                checked={formData.showEmail}
                onChange={() => setFormData({ ...formData, showEmail: !formData.showEmail })}
              />
            </label>
            <button type="submit">Save</button>
          </form>

          {/* ปุ่ม Cancel ถ้าไม่อยากแก้ไข */}
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default Profile;
