import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom'; // Correctly import Routes and Route
import Home from './components/Home';
import Profile from './components/Profile';
import Forum from './components/Forum';
import Blog from './components/Blog';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import { Box } from '@mui/material';

const App = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true); // State to manage sidebar

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen); // Toggle sidebar open/closed
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar open={sidebarOpen} toggleSidebar={toggleSidebar} />
      <Box
        sx={{
          flex: 1,
          padding: '20px',
          marginLeft: sidebarOpen ? 240 : 70, // Adjust margin based on sidebar state
          transition: 'margin-left 0.3s',
        }}
      >
        <Header />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/forum" element={<Forum />} />
          <Route path="/blog" element={<Blog />} />
        </Routes>
      </Box>
    </Box>
  );
};

export default App;
