import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, TextField, Button, Select, MenuItem, FormControlLabel, Checkbox } from '@mui/material';

const EditProfile = ({ user, onSave }) => {
  const [formData, setFormData] = useState({
    phone: '',
    birthday: '',
    bio: '',
    petTypes: [],
    showEmail: false,
  });
  const [profileImage, setProfileImage] = useState(null);

  useEffect(() => {
    const fetchUserProfile = async () => {
        try {
            const token = localStorage.getItem('token');
            console.log('Token:', token); // ตรวจสอบ Token
            if (!token) {
                alert('Please log in first');
                return;
            }
    
            const response = await axios.get('http://localhost:5000/pawpaw_test/user/profile', {
                headers: { 
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data',
                }
            });
            console.log('Response Data:', response.data); // ตรวจสอบข้อมูลที่เซิร์ฟเวอร์ตอบกลับ
    
            const userData = response.data;
            if (userData) {
                setFormData({
                    phone: userData.phone || '',
                    birthday: userData.birthday || '',
                    bio: userData.bio || '',
                    petTypes: userData.petTypes || [],
                    showEmail: userData.showEmail || false,
                });
                setProfileImage(userData.profileImage); // URL ของภาพ
            }
        } catch (error) {
            console.error('Error fetching profile:', error.response || error.message);
            alert('Failed to fetch profile');
        }
    };
    

    fetchUserProfile();
  }, []); // ใช้ [] เพื่อให้เรียกฟังก์ชันแค่ครั้งเดียวเมื่อโหลด Component

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };
  const handleSave = async () => {
    try {
        const token = localStorage.getItem('token');
        console.log('Token:', token); // ตรวจสอบ Token
        if (!token) {
            alert('Please log in first');
            return;
        }

        // สร้าง FormData และเพิ่มข้อมูล
        const data = new FormData();
        data.append('phone', formData.phone); // เพิ่มฟิลด์ phone
        data.append('bio', formData.bio); // เพิ่มฟิลด์ bio
        if (profileImage) {
            data.append('profileImage', profileImage); // เพิ่มไฟล์รูปภาพ
        }

        // ส่งคำขอไปยัง Backend
        const response = await axios.put('http://localhost:5000/pawpaw_test/user/profile', data, {
            headers: {
                Authorization: `Bearer ${token}`,
                'Content-Type': 'multipart/form-data',
            },
        });

        console.log('Response:', response.data); // ตรวจสอบการตอบกลับจาก API
        alert('Profile updated successfully');
    } catch (error) {
        console.error('Error updating profile:', error.response || error.message);
        alert('Failed to update profile');
    }
};

  return (
    <Box>
      <h2>Edit Profile</h2>
      <form>
        <TextField
          label="Phone"
          name="phone"
          value={formData.phone}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <TextField
          label="Birthday"
          name="birthday"
          type="date"
          value={formData.birthday}
          onChange={handleChange}
          fullWidth
          margin="normal"
          InputLabelProps={{ shrink: true }}
        />
        <TextField
          label="Bio"
          name="bio"
          value={formData.bio}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <Select
          multiple
          value={formData.petTypes}
          onChange={(e) => setFormData({ ...formData, petTypes: e.target.value })}
          fullWidth
          margin="normal"
        >
          {['Cat', 'Dog', 'Bird', 'Fish', 'Rabbit', 'Turtle', 'Other'].map((type) => (
            <MenuItem key={type} value={type}>
              {type}
            </MenuItem>
          ))}
        </Select>
        <FormControlLabel
          control={
            <Checkbox
              checked={formData.showEmail}
              onChange={(e) => setFormData({ ...formData, showEmail: e.target.checked })}
            />
          }
          label="Show Email on Profile"
        />
        <input
          type="file"
          onChange={(e) => setProfileImage(e.target.files[0])}
          accept="image/*"
        />
        <Button variant="contained" color="primary" onClick={handleSave}>
          Save Changes
        </Button>
      </form>
    </Box>
  );
};

export default EditProfile;
