import React, { useState, useEffect } from 'react';
import { Box, Typography, Fab } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import CreatePost from '../components/CreatePost';
import Post from '../components/Post';
import axios from 'axios';

const Forum = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/pawpaw_test/all');
        if (response.data.success) {
          setPosts(response.data.posts);
        }
      } catch (error) {
        console.error('Failed to fetch posts:', error);
      }
    };

    fetchPosts(); // เรียกใช้ fetchPosts เมื่อตัวคอมโพเนนต์ถูก mount
  }, []);

  const handleAddComment = (postId, newComment) => {
    setPosts((prevPosts) =>
      prevPosts.map((post) =>
        post._id === postId ? { ...post, comments: [...post.comments, newComment] } : post
      )
    );
  };

  return (
    <Box sx={{ padding: '20px' }}>
      <Typography variant="h4" sx={{ mb: 2 }}>
        Forum
      </Typography>
      <CreatePost onAddPost={(newPost) => setPosts([newPost, ...posts])} />
      <Box sx={{ width: '100%', maxWidth: '800px' }}>
        {posts.map((post) => (
          <Post
            key={post._id}
            post={post}
            onLike={() => console.log('Like functionality')}
            likedByUser={false}
            onAddComment={handleAddComment} // ส่งฟังก์ชัน onAddComment ไปยัง Post
          />
        ))}
      </Box>
      <Fab
        color="primary"
        aria-label="add"
        onClick={() => alert('Add new functionality coming soon')}
        sx={{ position: 'fixed', bottom: 20, right: 20 }}
      >
        <AddIcon />
      </Fab>
    </Box>
  );
};

export default Forum;
